-- ----------------------------
-- Table structure for `cms_article`
-- ----------------------------
DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `id` varchar(50) NOT NULL COMMENT '主键，文章ID',
  `article_region` varchar(50) DEFAULT '1' COMMENT '推荐到哪个专区。比如：头条、精选、最新、热门、评论最多等',
  `author` varchar(50) DEFAULT NULL COMMENT '原文作者',
  `publish_time` varchar(50) DEFAULT NULL COMMENT '发布时间',
  `title` varchar(255) DEFAULT NULL COMMENT '文章标题',
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键词',
  `description` varchar(255) DEFAULT NULL COMMENT '摘要',
  `cover_image` varchar(255) DEFAULT NULL COMMENT '封面图片',
  `copy_flag` smallint(6) DEFAULT '0' COMMENT '转载标志',
  `category_id` varchar(50) DEFAULT NULL COMMENT '频道栏目ID',
  `link` varchar(255) DEFAULT NULL COMMENT '原始链接',
  `static_url` varchar(255) DEFAULT NULL COMMENT '静态化后url',
  `tags` varchar(100) DEFAULT NULL COMMENT '标签',
  `hit` int(11) DEFAULT '0' COMMENT '点击数',
  `reply_num` int(11) DEFAULT '0' COMMENT '回复数',
  `up_vote` int(11) DEFAULT '0' COMMENT '点赞数',
  `down_vote` int(11) DEFAULT '0' COMMENT '差评数',
  `hot_flag` smallint(6) DEFAULT '0' COMMENT '热点标志',
  `new_flag` smallint(6) DEFAULT '0' COMMENT '新增标志',
  `comment_flag` smallint(6) DEFAULT '0' COMMENT '是否开启评论',
  `top_flag` smallint(6) DEFAULT '0' COMMENT '置顶标志',
  `favourite` int(11) DEFAULT '0' COMMENT '收藏数',
  `mission_id` varchar(50) DEFAULT NULL COMMENT '趴取任务的ID',
  `template_name` varchar(50) DEFAULT NULL COMMENT '生成静态页面的模板(cms_template表中的name)',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `available` smallint(6) DEFAULT '1' COMMENT '状态标志',
  `deleted` smallint(6) DEFAULT '0' COMMENT '删除标志',
  `extra1` varchar(255) DEFAULT NULL COMMENT '附加字段1',
  `extra2` varchar(255) DEFAULT NULL COMMENT '附加字段2',
  `extra3` varchar(255) DEFAULT NULL,
  `content` longtext,
  `content_markdown_source` longtext,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unq_link` (`link`) USING BTREE,
  KEY `index_title` (`title`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_article
-- ----------------------------
INSERT INTO `cms_article` VALUES ('2022052321345364653123', '1', null, null, '总书记和人民心贴心丨“是总书记的嘱托给我们克服困难的信心”', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A03BI900.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:34:54', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930780894/1000\"> </p> \n<div class=\"videoPlayerWrap\"></div> \n<script>window.DATA.videoArr.push({\"title\":\"“是总书记的嘱托给我们克服困难的信心”\",\"vid\":\"p3339mp6wvh\",\"img\":\"http://inews.gtimg.com/newsapp_ls/0/14930779669_640480/0\",\"desc\":\"“是总书记的嘱托给我们克服困难的信心”\"})</script> \n<p class=\"one-p\">新华社南宁5月22日电<strong>题：“是总书记的嘱托给我们克服困难的信心”</strong></p> \n<p class=\"one-p\">新华社记者向志强、黄浩铭</p> \n<p class=\"one-p\">2020年5月，广西环江毛南族自治县毛南族群众代表给习近平总书记写信，汇报当地毛南族实现脱贫的喜悦心情。随后，习近平总书记对毛南族实现整族脱贫作出重要指示。近日，新华社记者回访了曾经给总书记写信的群众代表谭美春，以下是她的自述和心声——</p> \n<p class=\"one-p\">两年前的5月，我们几个毛南族的乡亲们一起，写了我这辈子写得最长、寄得最远的一封信，收信人是习近平总书记。</p> \n<p class=\"one-p\">我们急切地想向总书记诉说心中的喜悦和感恩之情：在党和政府的帮扶下，我们这个人口较少民族，实现了整族脱贫！</p> \n<p class=\"one-p\">“得知毛南族实现整族脱贫、乡亲们生活有了明显改善，我感到很高兴。”——这封信寄出不久，总书记就作出重要指示。</p> \n<p class=\"one-p\">得知这一消息后，我太开心了。我们聚在村口的大榕树下用毛南话唱起《十颂党恩》：“鲤鱼伴水水更清，星星伴月月更明，我们过上好日子，永远不忘党恩情……”</p> \n<p class=\"one-p\">吃水不忘挖井人。我原本是一名贫困户，在脱贫政策的支持和扶贫干部的帮扶下，通过发展柑橘种植2016年实现了脱贫。尝到产业致富的甜头后，我带动包括14户贫困户在内的22户群众成立合作社，成为村里的致富带头人。2019年底，这14户贫困户全部实现脱贫。生活发生这么大的变化，做梦都想不到。</p> \n<p class=\"one-p\">总书记在指示中强调，希望乡亲们把脱贫作为奔向更加美好新生活的新起点，再接再厉，继续奋斗，让日子越过越红火。这两年，我们牢记总书记的嘱托，不懈努力，各方面都有了新的进步。</p> \n<p class=\"one-p\">最大的变化就是柑橘种植让大家钱包鼓起来了。为了提高柑橘的品质，县里经常派农业技术专家到田间地头教我们种植技术，还带我们去外面学习、对接客商、拓宽销售渠道。有技术、长见识后，经营观念也发生了变化。</p> \n<p class=\"one-p\">在发展产业的过程中，我们也遇到过困难。比如说，毛南山乡“八山一水一分田”，柑橘种植发展到一定规模后，再扩大就比较难了；再比如，柑橘价格波动大，群众有担忧。</p> \n<p class=\"one-p\">是总书记的嘱托给我们克服困难的勇气。我仔细分析了卖橘难的原因，原来价格上不去，主要是因为每年那段时间大量柑橘集中上市，我们没有竞争优势。我就想着能不能让柑橘提前上市，实现错峰销售。我又多次跑到县里的柑橘产业核心示范区取经，拿到“秘诀”后回来实践。</p> \n<p class=\"one-p\">我在自家120多亩柑橘园里先做试验，今年元旦，这批柑橘提前半个月上市，每斤价格比去年贵一块多钱的情况下，还被客商抢购一空。当红彤彤的柑橘装上运往北方的货车时，我心里甜滋滋的。现在这一模式推广到整个合作社，大家一起抱团发展。</p> \n<p class=\"one-p\">环江喀斯特是世界自然遗产，很多人说我们这个地方山美水美，是“神仙居住的地方”，但过去是个穷地方。现在，柑橘、桑蚕、香猪、油茶等特色产业在毛南山乡遍地开花。</p> \n<p class=\"one-p\">我想对总书记说，我们的日子一定会像红彤彤的柑橘一样，越来越甜，越过越红火。</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321345413681077', '1', null, null, '习近平“典”亮全人类共同价值｜安危不贰其志，险易不革其心', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A056GR00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:34:54', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">编者按：<strong>在博鳌亚洲论坛2022年年会开幕式上，习近平主席强调：“要践行共商共建共享的全球治理观，弘扬全人类共同价值，倡导不同文明交流互鉴。”</strong></p> \n<p class=\"one-p\">和平、发展、公平、正义、民主、自由，是全人类的共同价值。党的十八大以来，习近平主席在众多国际国内场合阐述全人类共同价值的内涵和外延，持续为全球经济发展注入动力，不断为世界形势向好发展提振信心。</p> \n<p class=\"one-p\">全人类共同价值是习近平新时代中国特色社会主义思想的一个重大理论创新，是推动构建人类命运共同体的理论基石。央视网《天天学习》栏目特别策划《习近平“典”亮全人类共同价值》系列，与您一起学习领会。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_match/0/14931375261/0\"> </p> \n<p class=\"one-p\"><strong>【释义】</strong></p> \n<p class=\"one-p\">《昌言》是东汉末哲学家仲长统的哲学政治著作。《后汉书·仲长统传》记载，仲长统“每论说古今及时俗行事，恒发愤叹息。因著论名曰《昌言》，凡三十四篇，十余万言”。原书已佚，《后汉书》《群书治要》《意林》等书中保存有某些片断。</p> \n<p class=\"one-p\">原典这段话讲的是臣下对君主应尽的义务。仲长统认为：“人之事君也，言无小大，无所愆也；事无劳逸，无所避也。”臣下事奉君主，言论要无错误，做事须不逃避。“其见识知也，则不恃恩宠而加敬；其见遗忘也，则不怀怨恨而加勤。”如果得到赏识，也不恃宠而骄，反而更加恭敬；如果怀才不遇，也不心怀怨恨，反而更加勤勉。“安危不贰其志，险易不革其心。”贰：改变。革：改变。“险易”义同“安危”。两句为同义反复，意思是无论是处于安全还是危险的境地，都不要改变自己的志向和操守。而且“孜孜为此，以没其身”，就这样孜孜不倦地终身持守。</p> \n<p class=\"one-p\"><strong>【解读】</strong></p> \n<p class=\"one-p\">今年是博鳌亚洲论坛成立21周年。21年来，博鳌亚洲论坛立足亚洲，面向世界，在凝聚亚洲共识、促进各方合作、推进经济全球化、推动构建人类命运共同体等方面建言献策，提出了许多富有价值的“博鳌方案”，作出了积极贡献。</p> \n<p class=\"one-p\">当下，世界之变、时代之变、历史之变正以前所未有的方式展开，给人类提出了必须严肃对待的挑战。人类还未走出世纪疫情阴霾，又面临新的传统安全风险；全球经济复苏仍脆弱乏力，又叠加发展鸿沟加剧的矛盾；气候变化等治理赤字尚未填补，数字治理等新课题又摆在我们面前。在此背景下，博鳌亚洲论坛2022年年会以“疫情与世界：共促全球发展，构建共同未来”为主题，具有重要意义。</p> \n<p class=\"one-p\">面对复杂变化的世界和诸多严峻挑战，人类社会向何处去？亚洲前途在哪里？对此时代之问，习近平主席高瞻远瞩地指出：“冲出迷雾走向光明，最强大的力量是同心合力，最有效的方法是和衷共济。”“只要我们携手同心、行而不辍，就一定能汇聚起合作共赢的伟力，战胜前进道路上的各种挑战，迎来人类更加光明美好的未来。”</p> \n<p class=\"one-p\">越是在困难的时候，越要坚定信心。在开幕式上，习近平主席以“四个共同”深刻阐释了应对当前一系列全球性挑战的中国方案：我们要共同守护人类生命健康，我们要共同促进经济复苏，我们要共同维护世界和平安宁，我们要共同应对全球治理挑战；同时，明确提出了促进世界安危与共的全球安全倡议。习主席的主旨演讲，把握历史规律，认清世界大势，犹如拨云见日，让中国方案、中国智慧、中国担当在国际讲坛上再一次大放异彩。</p> \n<p class=\"one-p\">同舟共济扬帆起，乘风破浪万里航。幸福和美好未来不会自己出现，成功属于勇毅而笃行的人。让我们坚持开放共赢，勇于变革创新，携手迎接挑战，向着构建人类命运共同体的目标不断迈进，共创亚洲和世界的美好未来！</p> \n<p class=\"one-p\">（作者 杨立新）</p> \n<p class=\"one-p\">（中央广播电视总台央视网）</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321345443793857', '1', null, null, '《足迹》第一集：过“五关”有多难', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A03GJU00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:34:54', null, '1', '0', null, null, null, '<!--导语--> \n<div class=\"videoPlayerWrap\"></div> \n<script>window.DATA.videoArr.push({\"title\":\"《足迹》第一集《过“五关”有多难》\",\"vid\":\"g33397826zf\",\"img\":\"http://puui.qpic.cn/vpic_cover/g33397826zf/g33397826zf_hz.jpg/640\",\"desc\":\"\"})</script> \n<p class=\"one-p\">1969年</p> \n<p class=\"one-p\">15岁的习近平下乡来到梁家河</p> \n<p class=\"one-p\">成为一名知青</p> \n<p class=\"one-p\">在这片黄土地上</p> \n<p class=\"one-p\">习近平度过了7年的青春岁月</p> \n<p class=\"one-p\">在梁家河</p> \n<p class=\"one-p\">习近平经历艰难生活的磨炼</p> \n<p class=\"one-p\">闯过了“五关”</p> \n<p class=\"one-p\">跳蚤关、饮食关、生活关、劳动关，还有思想关</p> \n<p class=\"one-p\">今天和大家分享</p> \n<p class=\"one-p\">习近平在延安插队过“五关”的故事</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321345465991544', '1', null, null, '上海：有序恢复正常生产生活秩序', null, null, null, '0', '2', 'https://new.qq.com/omn/TWF20220/TWF2022052300052200.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:34:55', null, '1', '0', null, null, null, '<!--导语--> \n<div class=\"videoPlayerWrap\"></div> \n<script>window.DATA.videoArr.push({\"title\":\"上海：绷紧疫情防控这根弦 有序恢复正常生产生活秩序\",\"vid\":\"q3339w6qend\",\"img\":\"http://inews.gtimg.com/newsapp_ls/0/14930300699_640480/0\",\"desc\":\"视频：上海：绷紧疫情防控这根弦 有序恢复正常生产生活秩序，时长约1分8秒\"})</script> \n<p class=\"one-p\">央视网消息（新闻联播）：上海市毫不动摇坚持“动态清零”总方针，强化系统观念和底线思维，安全有序推动公共交通逐步恢复运行，推动企业复工复产，稳妥有序恢复正常生产生活秩序。</p> \n<p class=\"one-p\">昨天（5月21日），上海本土确诊病例出院343例，解除医学观察的无症状感染者4549例。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930296149/1000\"> </p> \n<p class=\"one-p\">作为有序恢复正常生产生活秩序的重要工作之一，今天，上海4条轨道交通线、273条地面公交线路首批恢复运营。此次先行恢复运营的公共交通连接相关医院、机场和火车站等。</p> \n<p class=\"one-p\">在乘坐市内公共交通等交通工具时，相关部门引导市民支持配合、自觉遵守防疫要求，共同维护来之不易的防控成果。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930296153/1000\"> </p> \n<p class=\"one-p\">边防控、边复工，上海分批有序推动复工复产，重点产业链的复工复产也在稳步推进。</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321345487549251', '1', null, null, '各地统筹抓好疫情防控和经济社会发展', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A0213600.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:34:55', null, '1', '0', null, null, null, '<!--导语--> \n<div class=\"videoPlayerWrap\"></div> \n<script>window.DATA.videoArr.push({\"title\":\"各地统筹抓好疫情防控和经济社会发展\",\"vid\":\"s3339v1n1zz\",\"img\":\"http://puui.qpic.cn/vpic_cover/s3339v1n1zz/s3339v1n1zz_hz.jpg/640\",\"desc\":\"\"})</script> \n<p class=\"one-p\"><strong>央视网消息</strong>（新闻联播）：防疫不放松、经济要稳住。各地坚持“动态清零”，积极统筹抓好疫情防控和经济社会发展。</p> \n<p class=\"one-p\">在今天（5月22日）举行的北京市新冠肺炎疫情防控新闻发布会上，相关部门介绍，当前，北京市疫情防控正处于关键时期，要全面严防死守，以最快速度管控风险点、阻断传播链，区域核酸筛查做到应检尽检，密切接触者做到应判尽判、应转尽转、应隔尽隔。目前，北京重点涉疫区实行居家办公，减少人员流动，保障群众生活。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930379569/1000\"> </p> \n<p class=\"one-p\">为严防疫情扩散，四川广安市近日实施分区、分级、分类管控，针对新增感染者主要集中在邻水县的情况，当地正同步、多轮次开展核酸筛查。</p> \n<p class=\"one-p\">5月19日，辽宁大连中小学全部返校复学，复学后将继续严格落实防疫要求；5月20日，吉林市第四方舱医院最后一批患者治愈出院，正式休舱，至此，长春市和吉林市的所有方舱医院已全部休舱。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930379568/1000\"> </p> \n<p class=\"one-p\">疫情要防住、经济要稳住。眼下，广东徐闻的菠萝进入采收尾期，今年喜获丰收。疫情期间，为了保障徐闻菠萝及时销往外地，湛江市为菠萝运输流通户发放临时通行证，保障菠萝运输车行驶“绿色通道”，当地政府还通过农产品大数据平台帮助农户销售。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930379577/1000\"> </p> \n<p class=\"one-p\">江苏苏州针对制造业企业成立了稳定重点产业链、供应链的工作专班，协调解决企业实际困难，目前苏州12000多家规模以上工业企业全部实现正常生产。</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321470967457242', '1', null, null, '依法科学有序防控疫情', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01I7X00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:10', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">当前，全球疫情仍处于高位，病毒还在不断变异，疫情的最终走向还存在很大不确定性。面对国内疫情点多、面广、频发的特点，我们必须毫不动摇坚持“动态清零”总方针，科学防治、精准施策，从立法、执法、司法、守法各环节发力，全面提高依法防控、依法治理能力，为疫情防控工作提供有力法治保障。</p> \n<p class=\"one-p\">依法防控，离不开健全完备的法律体系。当前，我国已经构建起包括传染病防治法、突发公共卫生事件应急条例、疫苗管理法等在内的公共卫生法律法规体系，为核酸检测、开展流调、信息发布乃至维护市场稳定、保障劳动者合法权益等提供了坚实的法律支撑。同时，根据新形势、新情况，我国积极推动传染病防治法等相关法律的修订工作，进一步增强法律的现实针对性。</p> \n<p class=\"one-p\">“天下之事，不难于立法，而难于法之必行。”全民守法是依法防控的重要一环。当前，一些违反法律法规，不配合甚至破坏防疫的行为时有发生，妨碍着疫情防控大局。极少数确诊病例故意隐瞒行程轨迹和接触史，造成疫情传播扩散；一些营业场所未执行体温检测、扫码登记、环境消杀等规定，造成聚集性感染；个别网民传播散布涉疫不实信息，扰乱公共秩序……对此，需要筑牢群防群治的人民防线，引导广大人民群众丰富法律知识、增强法治意识，支持和配合疫情防控工作。</p> \n<p class=\"one-p\">知法才会守法。提升人民群众的相关法律知识储备，是依法防控的一个重要课题。各地区各部门需要将涉疫法治宣传与日常防疫工作相结合，加强疫情防控法治宣传，引导公众积极配合相关部门的防控举措，依法履行各项法律义务。一方面，积极创新手段，做好疫情防控中相关法律知识的普及。另一方面，将警示教育作为普法的重要内容。比如，在加大对各类妨害疫情防控行为执法司法力度的同时，及时向社会各界发布涉疫违法犯罪典型案例，起到警示犯罪、教育引导的作用。</p> \n<p class=\"one-p\">懂法更要用法。疫情防控期间，需要更好发挥法治的指引作用，让群众知悉哪些可为、哪些不可为。对每个人来说，面对疫情，积极学习、遵守传染病防治法等法律法规，配合和支持防疫工作，既有助于维护自己的生命健康与合法权益，也能为疫情防控增添力量。要积极主动学习涉疫法律知识，增强法治意识，做到知法懂法，自觉守法用法护法。</p> \n<p class=\"one-p\">疫情防控事关亿万人民生命安全和身体健康，也是人人有责、人人参与的全民行动。全社会共同营造尊法守法护法的良好氛围，依法科学有序防控疫情，才能筑牢法治堤坝，凝聚起战胜疫情的磅礴力量。</p> \n<p class=\"one-p\">《 人民日报 》( 2022年05月23日 05 版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321470987711888', '1', null, null, '熟悉的车轮转起来——上海跨区公共交通恢复首日直击', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A006XF00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:10', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">新华社上海5月22日电<strong>题：熟悉的车轮转起来——上海跨区公共交通恢复首日直击</strong></p> \n<p class=\"one-p\">新华社记者王辰阳、有之炘、贾远琨</p> \n<p class=\"one-p\">熟悉的报站声响起，5月22日，上海跨区公共交通启动恢复。恢复运营首日情况如何？记者探访公交线路、地铁站点、铁路枢纽等，发现现场秩序平稳，乘客扫描“场所码”流程顺畅，各运营单位做好疫情防控和复工运营的准备，适应市民多元化的出行需求和逐步回暖的客流量。在上海市内跨区公共交通启动恢复的同时，对外铁路客流也稳步回升。</p> \n<p class=\"one-p\"><strong>需求多元，乘公交就医、探亲、出游</strong></p> \n<p class=\"one-p\">迎着朝阳，早上7点，上海市劳模、公交车驾驶员孙杰驾驶着恢复运营后的第一班49路公交车驶出上海漕溪路公交枢纽站。作为上海首批恢复营运的273条公交线路之一，49路公交车途经15家医院，主要是为沿线居民提供就医便利。这趟公交车的乘客数量不多，但是到了复旦大学附属中山医院附近的站点，就有3名乘客下车。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14929839173/1000\"> </p> \n<p class=\"one-p\">5月22日，49路公交车驾驶员孙杰在站点等候乘客。新华社记者 王辰阳 摄</p> \n<p class=\"one-p\">记者在采访中发现，就医买药、探望亲人、出门采风成为上海市民出门乘坐公共交通的主要原因。乘客张俊欢说：“在新闻上听到公交恢复运营的消息，就特地赶上第一班公交车，去医院复诊。”</p> \n<p class=\"one-p\">张俊欢告诉记者，路上车不多，行程很顺利，驾驶员检查健康码、48小时内核酸阴性证明和体温情况非常认真，而且会确认每一个乘客都扫描了“场所码”才发车。据上海交通部门介绍，“场所码”的主要作用是一旦出现疫情传播的风险，能够便于相关部门开展快速流调和精准排查，帮助防止疫情扩散。</p> \n<p class=\"one-p\">作为上海公交最大的运营企业，上海久事公交集团方面介绍，集团恢复运营的111条线路，首班车均准点发出，22日全天计划出车477辆，截至22日14点，已经出车466辆，执行2311班次，运送乘客3218人次。</p> \n<p class=\"one-p\">“今天是公共交通恢复第一天，我就想出家门逛一逛。先坐了地铁10号线，现在坐71路公交车回家。”上海市民陈隽说，“我还有一些同事封控在家，他们看到我拍的照片都很羡慕，也希望大家能早日解封，都能出来走走看看。”</p> \n<p class=\"one-p\"><strong>做好准备，地铁又按下启动键</strong></p> \n<p class=\"one-p\">熟悉的地铁语音报站声，伴随着列车开行的隆隆声，一些拖着行李的乘客戴着口罩陆续出站，地铁员工们穿着防护服忙碌在各自的岗位……上海地铁恢复运营的首日，记者在3号线上海火车站站看到如上的场景。</p> \n<p class=\"one-p\">5月22日，上海地铁3号线、6号线、10号线、16号线率先恢复全线运营，运营时段为7点到20点，行车间隔约为20分钟。后续，上海地铁将根据疫情防控形势，适时调整运力，或增加恢复运营线路。</p> \n<p class=\"one-p\">上海地铁3号线连接上海火车站和上海南站枢纽，并且贯穿中心城区。上海地铁3号线上海火车站站副站长朱林20日回岗准备复工，22日早上7点准时回到了往日的工作节奏。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14929839174/1000\"> </p> \n<p class=\"one-p\">上海地铁3号线恢复运营首日车厢内情景。新华社记者 有之炘 摄</p> \n<p class=\"one-p\">在地铁恢复运营的首日，乘客并不算多。据记者观察，3号线上海火车站站的客流中，大部分是提着大件行李准备前往火车站的旅客。大部分乘客提前把“场所码”扫好，过安检只需几秒钟即可完成。</p> \n<p class=\"one-p\">“重新回到了岗位，看到地铁再次动起来，看到我们所熟悉的上海正在慢慢回归，我的内心十分激动。”在站台进行多次往返巡视的朱林表示，“期待更多线路恢复运营，我们会在地铁等待乘客们到来，送他们去想去的地方；也期待疫情能早日退却，届时乘客们可以脱下口罩，面对面给对方一个微笑。”</p> \n<p class=\"one-p\"><strong>客流回升，铁路枢纽运转有序</strong></p> \n<p class=\"one-p\">5月22日，上海虹桥火车站和上海站共开行旅客列车48列，预计发送旅客3.7万人次。上海虹桥火车站值班站长杨俊华介绍，5月份以来，铁路上海站列车开行量和旅客发送量逐渐回升，但防疫要求始终十分严格。</p> \n<p class=\"one-p\">为了维持旅客进站乘车秩序，在上海虹桥火车站的南、西广场，各设置多个检测点，符合离沪条件的旅客才能够进站乘车。记者采访发现，随着部分市内交通恢复运行，一部分旅客是乘坐地铁抵达上海虹桥火车站，从地铁出来的旅客也需要绕行至检测点出示相关个人信息，符合条件才能够进站。</p> \n<p class=\"one-p\">在候车大厅内，候车旅客数量已经明显回升。杨俊华介绍，由于上海市内交通才刚刚恢复，不少旅客为了保证不错过列车，会提前数个小时抵达车站，使得候车区域客流量较大。随着市内交通逐步恢复正常，建议旅客提前1个小时抵达车站即可，避免造成站区内外的拥堵。</p> \n<p class=\"one-p\">铁路部门表示，将根据相关防疫要求和旅客出行需求的变化，动态调整加开列车开行数量。</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471019379294', '1', null, null, '疫情防控吃劲阶段更不能有“躺平”幻想', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01GXL00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:10', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">【光明时评】</p> \n<p class=\"one-p\">近期，全国多地相继受奥密克戎变异毒株影响，出现新一轮疫情反弹，且传播速度加快，疫情防控形势严峻。而在历经两年多的抗疫后，不免有人产生懈怠情绪，“奥密克戎重症率、死亡率低，还有必要兴师动众吗”等想法不时涌现。科学证据表明，奥密克戎不是大号流感，新冠病毒今后如何变异仍面临很大不确定性。当前，疫情防控工作正处于“逆水行舟、不进则退”的关键时期和吃劲阶段，我们更不能抱有“躺平”幻想。</p> \n<p class=\"one-p\">鉴于我国的国情，“躺平”注定后果不堪设想。第七次全国人口普查结果显示，我国总人口数约为14亿1178万人，其中60岁以上人口达到2亿6402万人，占比为18.70%。奥密克戎变异株不仅传染性强、传播速度快，而且在传播过程中还有一定比例感染者是没有症状的，传播过程隐匿，传播风险加大。如果我们放任“躺平”，在短时间内势必导致大量人群感染。</p> \n<p class=\"one-p\">我国的地区发展不平衡，医疗资源总量不足，资源配置也不平衡。根据经济合作与发展组织的统计数据，2020年，中国每十万人ICU床位数仅为4.5张，而同期德国为28.2张、美国为21.6张、法国为16.4张、日本为13.8张，远远低于发达国家水平。特别是在一些经济不发达地区，基本的医疗卫生机构仍有待进一步完善。如果不把力量和资源重点放在核酸筛查、隔离等“防”的措施上，将压力都传导到“治”上，医疗卫生资源将面临严重挤兑，严重威胁有基础性疾病患者、老年人、儿童和孕妇等脆弱人群的健康，出现大量重症和死亡，其后果不堪设想。</p> \n<p class=\"one-p\">全球经济表现说明“躺平”无益经济改善。一些人抱有“躺平”幻想，是基于疫情防控措施可能影响到个人的工作生活，甚至影响整个经济社会发展，寄希望于“放开”能够带来个人境遇的改善，推动经济社会好转，但结果真会如此吗？在放任“躺平”的美国，目前不仅新冠死亡人数超过100万，同时也没有在经济上显示出亮眼“成绩单”。相关数据显示，今年第一季度，美国GDP下降约1.4%；欧元区19个成员国的GDP增幅相比上一季度收缩0.1%。虽然经济增长受多种因素影响，但“躺平”显然不会带来预想中的积极效果。</p> \n<p class=\"one-p\">而我国自新冠肺炎疫情暴发以来，率先控制住疫情，实现复工复产，2020年成为世界唯一实现经济正增长的主要经济体，2021年无论在保持经济增长还是疫情防控方面，取得的成绩有目共睹。当前，疫情对经济运行短期内造成较大冲击，但经济稳中向好、长期向好的基本面没有改变，我国的财政政策、货币政策和其他政策也有足够的空间和多样化的工具。随着疫情防控取得阶段性成效，稳定经济政策措施持续显效，定能推动我国经济发展迅速恢复活力。相反，如果我们放任“躺平”，不断涌现的大量人群感染或阻滞劳动力流动，企业生产难以正常运行，甚至导致供应链断裂，进而拖累经济社会发展，并严重影响个人生活。</p> \n<p class=\"one-p\">疫情防控吃劲阶段要放弃“躺平”，坚定信心。当前，疫情防控工作正处于“逆水行舟、不进则退”的关键时期和吃劲阶段，我们要丢掉幻想、坚定信心，毫不动摇坚持“动态清零”总方针。当然，面对病毒变异的新特点，我国也仍需不断加强防控能力建设，及时完善防控措施，加大对病毒变异的研究和防范力度，不搞简单化、一刀切，在疫情防控中做好群众基本生活保障和生活物资供应，保障好群众看病就医需求，积极回应人民关切，紧紧依靠人民群众打好人民战争。</p> \n<p class=\"one-p\">(作者：王娜，系北京市习近平新时代中国特色社会主义思想研究中心特约研究员、中国农业大学马克思主义学院教授)</p> \n<p class=\"one-p\">(来源：《光明日报》2022年5月23日02版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471045890435', '1', null, null, '系统观念是具有基础性的思想和工作方法（学苑论衡）', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01OLH00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:10', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930265271/1000\"> </p> \n<p class=\"one-p\">制图：张芳曼</p> \n<p class=\"one-p\">核心阅读</p> \n<p class=\"one-p\">系统观念是马克思主义认识论和方法论的重要范畴，是马克思主义政党基础性的思想和工作方法。新时代新征程上，我们必须更加自觉地坚持和运用系统观念，统筹兼顾、综合施策，加强前瞻性思考、全局性谋划、战略性布局、整体性推进。</p> \n<p class=\"one-p\">习近平总书记指出：“党的十八大以来，党中央坚持系统谋划、统筹推进党和国家各项事业，根据新的实践需要，形成一系列新布局和新方略，带领全党全国各族人民取得了历史性成就。在这个过程中，系统观念是具有基础性的思想和工作方法。”习近平新时代中国特色社会主义思想，坚持运用系统观念和系统方法全面统筹、协调推进社会主义现代化建设各方面各领域工作，提出一系列原创性治国理政新理念新思想新战略，形成了一个系统全面、逻辑严密、内涵丰富、内在统一的科学理论体系。在全面建设社会主义现代化国家的新征程上，我们必须更加自觉地坚持和运用系统观念，统筹兼顾、综合施策，加强前瞻性思考、全局性谋划、战略性布局、整体性推进，更好推动党和国家事业发展。</p> \n<p class=\"one-p\">系统观念是马克思主义认识论和方法论的重要范畴</p> \n<p class=\"one-p\">恩格斯在总结近现代自然科学成果的基础上指出：“关于自然界所有过程都处在一种系统联系中的认识，推动科学到处从个别部分和整体上去证明这种系统联系”。马克思运用系统方法和社会有机体思想，深入剖析资本主义社会这一复杂系统的内部结构和主要矛盾，科学揭示了资本主义社会的运行规律和发展趋势。</p> \n<p class=\"one-p\">根据唯物辩证法原理和现代系统论观点，系统是由诸多相互联系、相互作用的要素构成并与外部环境相互影响的具有特定结构和功能的有机整体。系统观念遵循整体性、结构性、层次性、开放性的思维方式。整体性主要强调把握系统整体与其组成要素之间的关系；结构性主要强调把握系统中诸要素之间的关系，包括要素之间的比例、结合方式等；层次性主要强调把握系统中不同层次之间的关系，研究不同层次的运行规律；开放性主要强调把握系统整体与外部环境之间的关系，越是有机的系统，其开放程度越高。概言之，系统观念要求在系统与要素、要素与要素、结构与层次、系统与环境之间相互联系、相互作用的动态过程中认识事物、把握规律，进而从总体上实现事物结构和功能优化。</p> \n<p class=\"one-p\">习近平总书记指出：“坚持唯物辩证法，就要从客观事物的内在联系去把握事物，去认识问题、处理问题。”从方法论角度看，坚持和运用系统观念就是要坚持发展地而不是静止地、全面地而不是片面地、系统地而不是零散地、普遍联系地而不是单一孤立地认识和处理问题。机械主义、形式主义、教条主义和经验主义的观点，都是形而上学的思想方法。正是从这个意义上说，系统观念是马克思主义认识论和方法论的重要范畴，是马克思主义政党基础性的思想和工作方法。</p> \n<p class=\"one-p\">中国共产党人始终坚持和运用系统观念</p> \n<p class=\"one-p\">马克思主义是中国共产党人的看家本领。在百年奋斗历程中，中国共产党人始终坚持和运用系统观念，善于从系统观念出发来认识和改造世界。</p> \n<p class=\"one-p\">在新民主主义革命时期，以毛泽东同志为主要代表的中国共产党人创立了毛泽东思想，运用系统观念来认识、把握中国革命的一系列重大问题。在《中国革命战争的战略问题》一书中，毛泽东同志强调：“因为懂得了全局性的东西，就更会使用局部性的东西，因为局部性的东西是隶属于全局性的东西的。”在《矛盾论》中，毛泽东同志强调中国共产党人“不但要研究每一个大系统的物质运动形式的特殊的矛盾性及其所规定的本质，而且要研究每一个物质运动形式在其发展长途中的每一个过程的特殊的矛盾及其本质”。在社会主义建设时期，毛泽东同志在《论十大关系》和《关于正确处理人民内部矛盾的问题》等著作中，从系统观念出发全面阐述社会主义建设应把握的重要关系，深入分析中国社会矛盾的变化。</p> \n<p class=\"one-p\">党的十一届三中全会以后，以邓小平同志为主要代表的中国共产党人创立了邓小平理论，用系统观念认识和把握改革开放大局，探索中国特色社会主义建设规律。邓小平同志指出：“要提倡顾全大局。有些事从局部看可行，从大局看不可行；有些事从局部看不可行，从大局看可行。归根到底要顾全大局。”以江泽民同志为主要代表的中国共产党人形成了“三个代表”重要思想，运用系统观念提出推进社会主义现代化建设必须处理好12个带有全局性的重大关系。以胡锦涛同志为主要代表的中国共产党人形成了以人为本、全面协调可持续发展的科学发展观，体现了系统的思想和工作方法。</p> \n<p class=\"one-p\">党的十八大以来，以习近平同志为核心的党中央在治国理政实践中，坚持系统谋划，统筹推进“五位一体”总体布局，协调推进“四个全面”战略布局，创立了习近平新时代中国特色社会主义思想这一系统全面、逻辑严密、内涵丰富、内在统一的科学理论体系。围绕运用系统观念治国理政，习近平总书记作出了一系列重要论述，比如：“全面深化改革是一项复杂的系统工程，需要加强顶层设计和整体谋划，加强各项改革关联性、系统性、可行性研究”“全面依法治国是一个系统工程，要整体谋划，更加注重系统性、整体性、协同性”“要从系统工程和全局角度寻求新的治理之道，不能再是头痛医头、脚痛医脚，各管一摊、相互掣肘，而必须统筹兼顾、整体施策、多措并举，全方位、全地域、全过程开展生态文明建设”；等等。党的十九届五中全会提出“十四五”时期经济社会发展必须遵循的原则，坚持系统观念是其中的一条重要原则。</p> \n<p class=\"one-p\">在新时代坚持好、运用好系统观念</p> \n<p class=\"one-p\">习近平总书记指出：“全面建成小康社会后，我们将开启全面建设社会主义现代化国家新征程，我国发展环境面临深刻复杂变化，发展不平衡不充分问题仍然突出，经济社会发展中矛盾错综复杂，必须从系统观念出发加以谋划和解决，全面协调推动各领域工作和社会主义现代化建设。”新时代新征程上，我们要坚持好、运用好系统观念，加强前瞻性思考、全局性谋划、战略性布局、整体性推进，推动党和国家事业不断从胜利走向胜利。</p> \n<p class=\"one-p\">加强前瞻性思考。不谋万世者，不足谋一时。习近平总书记强调：“要教育引导全党胸怀中华民族伟大复兴战略全局和世界百年未有之大变局，树立大历史观，从历史长河、时代大潮、全球风云中分析演变机理、探究历史规律，提出因应的战略策略，增强工作的系统性、预见性、创造性。”这其中蕴含着科学预见形势发展和未来走势的智慧，体现出透过现象看本质的思维方法。当前和今后一个时期，虽然我国发展仍然处于重要战略机遇期，但机遇和挑战都有新的发展变化，机遇和挑战之大都前所未有，总体上机遇大于挑战。我们要加强前瞻性思考，看清发展大势，分析面临的机遇和挑战、有利因素和不利因素，注重战略谋划，努力在危机中育先机、于变局中开新局。</p> \n<p class=\"one-p\">加强全局性谋划。不谋全局者，不足谋一域。习近平总书记强调：“领导干部想问题、作决策，一定要对国之大者心中有数，多打大算盘、算大账，少打小算盘、算小账，善于把地区和部门的工作融入党和国家事业大棋局，做到既为一域争光、更为全局添彩。”运用系统观念，就要牢固树立全局意识，正确认识大局，自觉服从大局，善于把本地区本部门的工作放到大局中进行思考和定位，把党中央的决策部署同本地区本部门的实际有机结合起来，不断增强总揽全局的能力。</p> \n<p class=\"one-p\">加强战略性布局。习近平总书记指出：“战略问题是一个政党、一个国家的根本性问题。”战略思维是从全局视野和长远眼光把握事物发展总体趋势和方向、客观辩证地思考和处理问题的科学思维。习近平总书记提出一系列原创性的治国理政新理念新思想新战略，如统筹中华民族伟大复兴战略全局和世界百年未有之大变局，统筹推进“五位一体”总体布局，协调推进“四个全面”战略布局，统筹发展和安全两件大事等，体现了高瞻远瞩的战略眼光、总揽全局的战略智慧。对各级领导干部而言，要提高战略思维能力，站在时代前沿和战略全局的高度观察、思考和处理问题，从政治上认识和判断形势，透过纷繁复杂的表面现象把握事物的本质和发展的内在规律，在解决突出问题中实现战略突破，在把握战略全局中推进各项工作。</p> \n<p class=\"one-p\">加强整体性推进。全面建设社会主义现代化国家是一个系统工程，必须坚持综合平衡、整体推进。习近平总书记强调：“重大改革都是牵一发而动全身的，更需要全面考量、协调推进。”党的十八大以来，我们党把协调发展放在更加重要的位置，要求正确处理发展中的重大关系，促进经济社会协调发展。习近平总书记指出：“我们要学会运用辩证法，善于‘弹钢琴’，处理好局部和全局、当前和长远、重点和非重点的关系，在权衡利弊中趋利避害、作出最为有利的战略抉择。从当前我国发展中不平衡、不协调、不可持续的突出问题出发，我们要着力推动区域协调发展、城乡协调发展、物质文明和精神文明协调发展”。领导干部必须在把情况搞清楚的基础上，统筹兼顾、综合平衡，突出重点、带动全局，增强发展的整体性，推动党和国家事业不断迈上新台阶。</p> \n<p class=\"one-p\">(作者为北京大学教授)</p> \n<p class=\"one-p\">《 人民日报 》( 2022年05月23日 09 版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471075829120', '1', null, null, '把文艺创造写到民族复兴的历史上', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A022BV00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:11', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">这是百年党史长卷极具标志性的重要节点，是中国文艺划时代的耀眼坐标：</p> \n<p class=\"one-p\">80年前，毛泽东同志《在延安文艺座谈会上的讲话》为中国革命文艺指明正确方向，中国新文艺发展道路有了前行明灯。</p> \n<p class=\"one-p\">文化兴国运兴，文化强民族强。中国共产党从成立之日起就把建设民族的科学的大众的中华民族新文化作为自己的使命，积极推动文化建设和文艺繁荣发展。</p> \n<p class=\"one-p\">当代中国，江山壮丽，人民豪迈，前程远大。新时代为我国文艺繁荣发展提供了前所未有的广阔舞台。</p> \n<p class=\"one-p\">眼纳千江水、胸起百万兵。广大文艺工作者与党同心同向、与时代同步同行、为人民抒写抒情抒怀，奋力书写民族复兴壮阔史诗。</p> \n<p class=\"one-p\">从延安出发</p> \n<p class=\"one-p\">中国国家博物馆精心打造了一座长12米的延安文艺座谈会参会者大合影墙。巨幅黑白影像中，一个个朴素而振奋的面庞，定格了时光。</p> \n<p class=\"one-p\">1942年的中国，正值民族生死存亡关键时刻。这一年5月举行的延安文艺座谈会，深刻影响了20世纪中国文艺的发展轨迹。</p> \n<p class=\"one-p\">毛泽东同志主持召开了由文艺工作者和中央各部门负责人参加的文艺座谈会，发表了重要讲话。他鲜明地提出，“为什么人的问题，是一个根本的问题，原则的问题”，“我们的文学艺术都是为人民大众的”。他向广大文艺战士发出号召：有出息的文学家艺术家，必须到群众中去，必须长期地无条件地全心全意地到工农兵群众中去，到火热的斗争中去。</p> \n<p class=\"one-p\">参会者、作家刘白羽后来形容：“我甚至感到整个世界的重量此时都凝聚在中国的西北高原和这一片黄土地之上，凝聚在这会场里每个人的心上。从此以后，中国的文学、艺术，中国的文艺工作者发生了脱胎换骨的巨大变化。”</p> \n<p class=\"one-p\">到人民中间去，到火热的斗争中去，解放区文艺工作者纷纷奔赴抗战前线，深入农村、部队、工厂，创作了一大批代表“延安文艺”的作品：长篇小说《太阳照在桑干河上》《暴风骤雨》，短篇小说《小二黑结婚》《荷花淀》，民族歌剧《白毛女》，民歌《东方红》《翻身道情》……直到今天，这些红色经典仍给人启迪。</p> \n<p class=\"one-p\">文艺为人民，是一条从延安出发贯穿新中国文艺史的精神脉络。</p> \n<p class=\"one-p\">邓小平同志说：“我们的文艺属于人民”，“人民是文艺工作者的母亲”。江泽民同志要求广大文艺工作者“在人民的历史创造中进行艺术的创造，在人民的进步中造就艺术的进步”。胡锦涛同志强调：“只有把人民放在心中最高位置，永远同人民在一起，坚持以人民为中心的创作导向，艺术之树才能常青。”</p> \n<p class=\"one-p\">历史翻开新的一页。2014年10月15日，习近平总书记主持召开文艺工作座谈会，站在实现中华民族伟大复兴中国梦的战略高度，深刻阐述和科学回答了在新的历史条件下如何繁荣发展社会主义文艺的一系列重大问题，为新时代中国特色社会主义文化发展锚定价值航向，擘画中国文艺发展宏伟蓝图。</p> \n<p class=\"one-p\">“社会主义文艺，从本质上讲，就是人民的文艺”；</p> \n<p class=\"one-p\">“文艺创作方法有一百条、一千条，但最根本、最关键、最牢靠的办法是扎根人民、扎根生活”；</p> \n<p class=\"one-p\">“文艺不能在市场经济大潮中迷失方向，不能在为什么人的问题上发生偏差，否则文艺就没有生命力”；</p> \n<p class=\"one-p\">“我国作家艺术家应该成为时代风气的先觉者、先行者、先倡者，通过更多有筋骨、有道德、有温度的文艺作品，书写和记录人民的伟大实践、时代的进步要求，彰显信仰之美、崇高之美”；</p> \n<p class=\"one-p\">…………</p> \n<p class=\"one-p\">相隔72年时空，两次文艺座谈会遥相呼应。</p> \n<p class=\"one-p\">一样的群英荟萃，一样的畅所欲言，一样的心系人民，一样的家国情怀。两次座谈会一脉相承又与时俱进，成为中国文艺发展历程中具有标志性意义的里程碑。</p> \n<p class=\"one-p\">沿着不可逆转的伟大复兴之路踔厉前行，开启伟大复兴的文艺新征程，新时代文艺工作者坚持“二为”方向、“双百”方针，坚持创造性转化、创新性发展，在更加广阔的舞台上为人民放歌、为时代铸魂，更好构筑中国精神、中国价值、中国力量。</p> \n<p class=\"one-p\">始终如一的人民情怀</p> \n<p class=\"one-p\">高亢的陕北民歌、欢快的安塞腰鼓与悠长浑厚的呼麦，交织回荡在陕西延安。2022年5月20日，内蒙古乌兰牧骑来到新中国文艺的出发地，为当地百姓演出，从延安精神中汲取力量。</p> \n<p class=\"one-p\">以人民为中心，是历史的回响，更是时代的命题。</p> \n<p class=\"one-p\">习近平总书记指出，源于人民、为了人民、属于人民，是社会主义文艺的根本立场，也是社会主义文艺繁荣发展的动力所在。</p> \n<p class=\"one-p\">60多年来，一代代乌兰牧骑队员在戈壁、草原上辗转跋涉，以天为幕布，以地为舞台，传递党的声音和关怀，为广大农牧民送去欢乐，成为全国文艺战线的一面旗帜。习近平总书记曾不止一次为他们点赞，并勉励他们“永远做草原上的‘红色文艺轻骑兵’”。乌兰牧骑的长盛不衰表明，人民需要艺术，艺术也需要人民。</p> \n<p class=\"one-p\">“江山就是人民，人民就是江山，为人民书写是作家的职责。”鲁迅文学奖得主、作家铁流以柳青写作《创业史》为榜样，深入农村进行长达14年的“抢救性”采访，写出长篇纪实文学《靠山》再现动人的支前故事，获评2021年度“中国好书”。他由衷感叹：“深入生活、扎根人民就像打一口井，井打得有多深，水就有多甜。”</p> \n<p class=\"one-p\">坚持以人民为中心的创作导向，深入生活聚焦现实，潜心创作深耕不辍，我国文艺事业格局一新、境界一新、气象一新。文艺园地百花齐放、硕果累累——</p> \n<p class=\"one-p\">电影《我和我的祖国》《长津湖》、电视剧《山海情》《觉醒年代》、长篇小说《人世间》《主角》、舞剧《永不消逝的电波》、话剧《谷文昌》、豫剧《焦裕禄》……有温度、有筋骨、有力量的优秀作品层出不穷，激发广泛情感共鸣。</p> \n<p class=\"one-p\">把满足人民精神文化需求作为文艺和文艺工作的出发点和落脚点，广大文艺工作者火热开展“到人民中去”“送欢乐下基层”等志愿服务活动，深入400多个地市、区、县，累计培训基层文艺骨干20余万人，让文艺之光照亮基层每一个角落。</p> \n<p class=\"one-p\">北京、河北、内蒙古、山东、河南、湖北、新疆等七省区市推进“文艺进万家 健康你我他”新时代文明实践文艺志愿服务项目试点，组织开展各类文艺志愿服务活动8000余场，为新时代文明实践中心建设和丰富基层人民群众精神文化生活作出了积极贡献。</p> \n<p class=\"one-p\">近日，文化和旅游部将组织纪念毛泽东同志《在延安文艺座谈会上的讲话》发表80周年主题展，同时举办优秀剧目网络展播，歌剧《白毛女》、京剧《三打祝家庄》、秦腔《王贵与李香香》等经典剧目再度呈现；国家广播电视总局举办“纪念《在延安文艺座谈会上的讲话》发表80周年——推动新时代广播电视和网络视听文艺繁荣发展理论研讨会”；中国文联组织召开纪念毛泽东同志《在延安文艺座谈会上的讲话》发表80周年理论研讨会，还将组织知名美术、摄影艺术家赴陕西延安、照金开展采风创作活动；中国作协举办“向人民大地”特展等系列纪念活动，展现经典作品，彰显时代新貌……</p> \n<p class=\"one-p\">岁月如歌，江山如画；精神如炬，信念如磐。复兴路上，“文艺为人民”的旗帜愈发鲜艳!</p> \n<p class=\"one-p\">吹响时代前进号角</p> \n<p class=\"one-p\">文艺是时代前进的号角，最能代表一个时代的风貌，最能引领一个时代的风气。</p> \n<p class=\"one-p\">庆祝中国共产党成立100周年文艺演出《伟大征程》浓墨重彩展现中国共产党百年来走过的光辉历程，振奋精神、激发斗志；</p> \n<p class=\"one-p\">庆祝中华人民共和国成立70周年文艺晚会《奋斗吧 中华儿女》再现一代又一代中华儿女为民族独立、国家富强不懈奋斗的伟大历程，引发巨大共鸣；</p> \n<p class=\"one-p\">庆祝改革开放40周年文艺晚会《我们的四十年》生动刻画改革开放带来气势恢宏的山河巨变，展现全民族昂首阔步新征程的豪迈情怀；</p> \n<p class=\"one-p\">…………</p> \n<p class=\"one-p\">在艺术的回望与传承中，家国情怀的印记清晰可见。</p> \n<p class=\"one-p\">一方舞台，演绎百年风华。中国共产党百年华诞之际，民族歌剧《党的女儿》、京剧《红灯记》、芭蕾舞剧《红色娘子军》等一大批反映党的百年历程的舞台艺术经典再度亮相新时代舞台，铺展开斑斓的历史画卷。</p> \n<p class=\"one-p\">为时代画像、为时代立传、为时代明德。</p> \n<p class=\"one-p\">从讲述精准扶贫故事的歌舞剧《大地颂歌》，到全景描写中国脱贫攻坚的长篇报告文学作品《乡村国是》；从表现伟大抗疫精神的话剧《人民至上》，到抗击疫情主题歌曲《坚信爱会赢》……树立大历史观、大时代观，以社会主义核心价值观为灵魂，新时代文艺书写时代之美，彰显信仰之力，弘扬人间正道。</p> \n<p class=\"one-p\">在精神文明建设“五个一工程”等国家级奖项的引导下，一大批思想精深、艺术精湛、制作精良的文艺作品脱颖而出。</p> \n<p class=\"one-p\">“面对每一个重要历史关头和重大历史事件，文艺工作者从来没有置身事外，从来没有缺席缺位，始终与国家民族前途命运紧密相连、休戚与共。”中国文联主席、中国作协主席铁凝说。</p> \n<p class=\"one-p\">“文艺要塑造人心，创作者首先要塑造自己。”牢记习近平总书记的谆谆嘱托，越来越多的文艺工作者在创作实践中磨炼人品、锻造艺品，德艺双馨的文艺队伍不断充实壮大。</p> \n<p class=\"one-p\">“‘演员’这两个字沉甸甸，饱含前辈挣得的荣誉、时代赋予的责任、人民殷切的期待。”电影表演艺术家李雪健说，为人民奉献精神食粮，演员责无旁贷；为民族贡献精神力量，演员大有可为。</p> \n<p class=\"one-p\">铸就中华文化新辉煌</p> \n<p class=\"one-p\">壬寅年伊始，连接东西方文明的一场新春“冬奥之约”，架起中华文明与世界文明交流互鉴的桥梁。</p> \n<p class=\"one-p\">二十四节气、黄河之水、中国结、迎客松、折柳寄情、雪花主题歌……饱含圆融和合等中国理念的北京2022年冬奥会开、闭幕式惊艳世界。中华文化和冰雪元素交相辉映，人类命运共同体的主题贯穿始终。时光定格下东方大国的奋进雄姿，更激荡中华文化的自信昂扬。</p> \n<p class=\"one-p\">中华优秀传统文化是中华民族的突出优势，是我们在世界文化激荡中站稳脚跟的根基，必须结合新的时代条件传承和弘扬好。</p> \n<p class=\"one-p\">党的十八大以来，习近平总书记高度重视传承和弘扬中华优秀传统文化，鲜明提出创造性转化、创新性发展的原则。</p> \n<p class=\"one-p\">“以古人之规矩，开自己之生面”。广大文艺工作者坚持守正创新，坚守中华文化立场，传承中华文化基因，努力推动收藏在博物馆里的文物、陈列在广阔大地上的遗产、书写在古籍里的文字“活”起来。</p> \n<p class=\"one-p\">六季《中国诗词大会》收视超过30亿人次，《永乐大典》回归再造见证中华儿女传续文脉，系列图书《中华传统文化百部经典》深受读者欢迎，《国宝档案》《唐宫夜宴》等传统文化节目频频“破圈”，舞剧《只此青绿》、昆曲《牡丹亭》等多部力作叫好叫座、一票难求，故宫、敦煌等国潮文创IP让生活更有“文艺范儿”……</p> \n<p class=\"one-p\">《关于支持戏曲传承发展的若干政策》出台，全面促进戏曲传承发展；全国地方戏曲南方会演、全国梆子声腔优秀剧目展演、戏曲百戏(昆山)盛典等戏曲演出活动，推出了一系列优秀戏曲剧目；中国京剧像音像工程录制了全国优秀京剧演员的380余部代表性剧目；“名家传戏”活动的开展、中国京剧优秀青年演员研究生班的举办，培养了一大批青年戏曲人才。</p> \n<p class=\"one-p\">坚定文化自信，铸就中华文化新辉煌。新时代生动的艺术实践传承和弘扬中华美学精神，张扬中国特色、中国风格、中国气派。</p> \n<p class=\"one-p\">2022年的这个5月，中央广播电视总台将推出大型文化节目《从延安出发》，联合10余家国家级文艺院团、院校及演出机构，展演话剧、歌剧、舞剧等多类型精品力作，展示新时代文艺成果。节目中，平均90余岁的前辈艺术家与“90后”青年追寻者“时空对话”，赓续红色血脉，凝聚文艺力量。</p> \n<p class=\"one-p\">大鹏一日同风起，扶摇直上九万里。新时代中国文艺牢记“国之大者”，勇攀艺术高峰，把文艺创造写到民族复兴的历史上、写在人民奋斗的征程中。</p> \n<p class=\"one-p\">(新华社北京5月22日电 记者周玮、施雨岑、史竞男、王思北、蔡馨逸)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471099367639', '1', null, null, '从南到北陆续开镰 确保夏粮颗粒归仓', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01M9L00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:11', null, '1', '0', null, null, null, '<!--导语--> \n<div class=\"videoPlayerWrap\"></div> \n<script>window.DATA.videoArr.push({\"title\":\"从南到北陆续开镰 确保夏粮颗粒归仓\",\"vid\":\"i3339vj5o56\",\"img\":\"http://puui.qpic.cn/vpic_cover/i3339vj5o56/i3339vj5o56_hz.jpg/640\",\"desc\":\"\"})</script> \n<p class=\"one-p\"><strong>央视网消息</strong>（新闻联播）：眼下，小麦陆续进入成熟期，我国从南到北陆续开镰，各地抓实抓细夏收夏种夏管各项工作，确保夏粮颗粒归仓，夯实全年粮食生产基础。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930237439/1000\"> </p> \n<p class=\"one-p\">河南是我国小麦主产区，这两天，在小麦成熟较早的南阳市罗庄镇，小麦收割迎来“第一镰”。村民们说，虽然去年播种比较晚，但因为今年雨水充足墒情较好，加之小麦中后期管理到位，今年每块地亩产能达到800斤左右。在四川成都平原，小麦陆续进入收获季。</p> \n<p class=\"one-p\">在江西九江，15万亩小麦收割已完成六成，预测产量较去年增加5%左右；在浙江金华，这几天抢抓晴好天气，小麦农场纷纷开镰收割。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930237448/1000\"> </p> \n<p class=\"one-p\">今年陕西小麦播种面积1440万亩，主要集中在关中平原。现在，农业农村部门近千名干部包镇进村，提供技术帮扶指导，重点抓好以小麦条锈病、赤霉病为主的病虫害监测和应急防治。</p> \n<p class=\"one-p\">与此同时，各地多举措组织好跨区机收作业，全力保障小麦机收顺利开展。在江苏宿迁，农业农村部门将服务前移，采取上门年检、上门办理的方式，为跨区作业的农机手办理跨区作业证；湖北襄阳、河北邯郸等地在高速公路出入口开设农机转运“绿色通道”，设置“三夏”农机跨区作业接待服务站，为跨区作业人员提供核酸、属地登记备案等“一站式服务” 。</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471121572458', '1', null, null, '一线调研：同心协力 助小微企业闯难关', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01N5900.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:11', null, '1', '0', null, null, null, '<!--导语--> \n<div class=\"videoPlayerWrap\"></div> \n<script>window.DATA.videoArr.push({\"title\":\"一线调研：同心协力 助小微企业闯难关\",\"vid\":\"i3339s5o6p4\",\"img\":\"http://puui.qpic.cn/vpic_cover/i3339s5o6p4/i3339s5o6p4_hz.jpg/640\",\"desc\":\"\"})</script> \n<p class=\"one-p\"><strong>央视网消息</strong>（新闻联播）：今天（5月22日）的一线调研，我们来看江西省南昌市的一家连锁小面馆在疫情冲击下，如何通过自身努力和政府扶持坚持发展、渡过难关。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14930248467/1000\"> </p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471142124220', '1', null, null, '坚持全面、辩证、长远看待经济运行', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01ECE00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:11', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">综合当前和长远，中国经济运行特点可以概括为“形有波动、势仍向好”。要看到，部分数据的“形有波动”集中在短期</p> \n<p class=\"one-p\">将稳增长放在更突出位置，要根据客观实际和群众需求做决策、出实招，靠实打实的政策红利来助企纾困，稳住市场预期，稳住宏观经济大盘</p> \n<p class=\"one-p\">正确研判中国经济，要有战略定力和辩证眼光。坚持从世界看中国、从全局看局部、从未来看当下，才能真正把握规律、增强信心。日前公布的4月份经济数据显示，受国际环境更趋复杂严峻和国内疫情冲击明显的超预期影响，经济下行压力进一步加大。但这种影响是短期的、外在的，对于中国这样一个规模巨大、变量众多的经济体来说，出现短期波动是暂时的，我们要坚持全面、辩证、长远看待经济运行的“形”与“势”。</p> \n<p class=\"one-p\">综合当前和长远，中国经济运行特点可以概括为“形有波动、势仍向好”。要看到，部分数据的“形有波动”集中在短期。疫情对经济运行造成冲击，4月份有些指标有所回落、增速有所放缓。更要认识到，经济发展“势仍向好”主要看长期。开年以来，一季度4.8%的经济增长率位居主要经济体前列，而物价涨幅处在较低水平，4月份CPI同比上涨2.1%，环比上涨0.4%；货物进出口持续增长，贸易结构继续优化，1—4月份货物进出口总额同比增长7.9%；主要群体就业基本稳定，4月份25—59岁人口调查失业率为5.3%。从长远来看，我国经济稳中向好、长期向好的基本面没有改变，经济潜力足、韧性大、活力强、回旋空间大、政策工具多的基本特点没有变。随着高效统筹疫情防控和经济社会发展成效显现，国民经济有望企稳回升。</p> \n<p class=\"one-p\">在短期波动的情况下，我们看到经济新动能在较快成长。尽管疫情冲击下工业生产放缓，但高技术制造业仍保持较快增长，4月份增加值同比增长4%，产业升级发展态势没有改变。受到疫情影响的消费和投资也展现积极因素，基本生活类商品销售持续增长，1—4月份全国网上零售额38692亿元，同比增长3.3%，基础设施投资同比增长6.5%，制造业投资增长12.2%，其中高技术制造业和高技术服务业投资分别增长25.9%和13.2%，社会领域中卫生、教育投资分别增长24.9%和12.5%。可以说，我国经济转型升级、高质量发展的大势没有改变，稳定宏观经济大盘、实现发展预期目标有利条件较多，我们有足够的底气、能力、智慧战胜各种风险考验，保持经济持续健康发展。</p> \n<p class=\"one-p\">继续保持长期向好的“势”，也要积极应对好短期波动调整的“形”，解决短期波动反映的现实问题。一方面，要有效管控重点风险，守住不发生系统性风险底线。对4月份一些指标波动反映的问题要高度重视，抓紧谋划增量政策工具，让稳增长政策进一步显效发力。另一方面，要坚持稳字当头、稳中求进，高效统筹疫情防控和经济社会发展，落实好“疫情要防住、经济要稳住、发展要安全”的要求。随着疫情防控取得阶段性成效，复工复产有序推进，稳定经济政策措施持续显效，发用电量等一些先行指标已出现积极变化，经济运行会很快回归正常轨道。将稳增长放在更突出位置，要根据客观实际和群众需求做决策、出实招，靠实打实的政策红利来助企纾困，稳住市场预期，稳住宏观经济大盘。</p> \n<p class=\"one-p\">疾风知劲草，烈火见真金。越是面对超预期影响，越是要保持战略定力，坚定必胜信心，集中力量做好自己的事。各地区各部门要把思想和行动统一到党中央决策部署上来，担负起稳定宏观经济的责任，加快落实已经确定的政策，积极推出有利于经济稳定的政策，提振市场预期，确保经济运行在合理区间。</p> \n<p class=\"one-p\">《 人民日报 》( 2022年05月23日 05 版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471164057166', '1', null, null, '积极努力稳住外贸外资基本盘', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01I6V00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:12', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">【光明论坛】</p> \n<p class=\"one-p\">今年一季度，我国货物贸易进出口总值同比增长10.7%，实际使用外资同比增长25.6%，外贸外资双双以两位数增长实现“开局稳”。同时也要看到，当前，新冠肺炎疫情和乌克兰危机导致风险挑战增多，受内外部多重因素影响，我国稳外贸稳外资的压力显著增加。鉴于此，近日召开的中共中央政治局会议强调，“疫情要防住、经济要稳住、发展要安全”，同时指出，“要坚持扩大高水平对外开放，积极回应外资企业来华营商便利等诉求，稳住外贸外资基本盘”。5月9日召开的全国促进外贸外资平稳发展电视电话会议提出，要深入学习贯彻习近平总书记重要指示精神，积极努力稳住外贸外资基本盘。</p> \n<p class=\"one-p\">开放发展是国家繁荣发展的必由之路。党的十八大以来，我国以共建“一带一路”为引领，推动开放型经济新体制建设迈上新台阶，以更加开放的心态、更加自信的步伐融入世界经济，国家经济实力不断跃上新台阶。2021年，我国经济总量已经接近美国的77%，占世界经济的比重超过18%。目前我国已经形成制造业基本开放、农业服务业稳步持续开放的新格局，为外贸外资企业提供了更加广阔的发展空间。在新的时代条件下，积极努力稳住外贸外资基本盘，需要准确把握开放发展和经济安全的辩证法，加强和完善外贸外资服务保障机制，不断优化我国外贸外资发展环境。</p> \n<p class=\"one-p\">发展和安全是一体之两翼、驱动之双轮。开放发展与经济安全之间互为条件、彼此支撑，存在着紧密且复杂的辩证关系。一方面，对外开放和经济发展是经济安全的物质基础和根本保障。开放带来进步，封闭必然落后。在全球化的21世纪，封闭的国家不可能获得经济的长期发展，而经济发展长期滞后，抗冲击能力也必然低下，这就是最大的不安全。另一方面，经济安全是对外开放和经济发展的必要前提。对外开放要把握好度，要与本国经济安全条件和抗冲击能力相匹配。缺乏条件、不顾后果的超前开放，不仅不可能带来经济的稳定发展，还很可能危害和拖累经济发展。</p> \n<p class=\"one-p\">首先，要避免经济安全泛化，在确保国家经济安全前提下实行更加积极主动的开放战略。做好稳外贸工作，当务之急是打通堵点难点，保障外贸领域生产流通稳定，重点保障外贸货物运输高效畅通，全力保障外贸产业链供应链完整和稳定。中长期来说，要重点做好三项工作：一是进一步推进贸易与投资自由化、便利化，鼓励发展同线同标同质产品，推动内外贸一体化；二是适时制定全国版跨境服务贸易负面清单，做大做强数字服务、特色服务等出口基地，培育服务贸易新增长点；三是积极推进加入《数字经济伙伴关系协定》和《全面与进步跨太平洋伙伴关系协定》，加快构建面向全球的高标准自由贸易区网络。</p> \n<p class=\"one-p\">做好稳外资工作，当务之急是加强和完善外贸外资协调机制，积极回应外资企业反映的新诉求，并及时协调解决，帮助其实现稳定有序经营，切实稳住现有外资企业。中长期而言，要重点做好两项工作：一是进一步缩减外资准入负面清单，加快推进制度型开放，促进内外资市场主体公平竞争。二是对接国际高标准经贸规则，统筹推进自由贸易试验区、海南自由贸易港、内陆开放型经济试验区等各类开放平台建设，打造营商环境更优的对外开放新高地，以优质市场环境吸引更多国际资本在我国投资兴业。</p> \n<p class=\"one-p\">其次，要防止经济安全虚化，构筑安全保障体系，在开放发展中维护好经济安全。一是通过全面落实公平竞争审查制度、调整优化外商投资安全审查范围等，健全对外资的国家安全审查制度。二是完善相关法律法规体系，加强对数字经济领域的反垄断和反不正当竞争，有效防范风险，维护市场公平竞争。三是谨慎放宽特定行业的外资市场准入，对于涉及国家安全的敏感领域继续保留外资准入限制。</p> \n<p class=\"one-p\">不拒众流，方为江海。改革开放40多年来，对外开放推动我国经济发展创造了举世瞩目的“中国奇迹”。面对当前错综复杂的形势，我们要坚定构建更高水平的开放型经济新体制，持续深化商品和要素流动性开放，稳住外贸外资的基本盘，为促进世界经济复苏和建设开放型世界经济继续做出中国的重要贡献。</p> \n<p class=\"one-p\">(作者：马相东，系北京市习近平新时代中国特色社会主义思想研究中心特约研究员)</p> \n<p class=\"one-p\">(来源：《光明日报》2022年5月23日02版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471197489266', '1', null, null, '长风破浪会有时！政策定力护航中国经济', null, null, null, '0', '2', 'https://new.qq.com/omn/20220522/20220522A03BX800.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:12', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">“读懂中国经济”系列网评之三</p> \n<p class=\"one-p\">马若虎</p> \n<p class=\"one-p\">当前，在疫情反复、地缘政治紧张、通胀高企等多重压力下，世界经济不确定性增加。作为全球第二大经济体、最大贸易国和主要外资流入国，中国面临多重挑战，但中国应对挑战的方案和行动，却为世界带来更多确定性。</p> \n<p class=\"one-p\">财政货币政策以就业优先为导向，稳住经济大盘；今年3.65万亿元新增专项债额度中用于项目建设的额度全部下达，支持有效投资发挥关键作用，20条促消费政策发挥消费对经济循环的牵引带动作用；北京今年4月推出帮扶企业18条措施，建立了稳定产业链供应链协调机制；上海建立了重点企业“白名单”制度，有序推进工业企业复工复产……</p> \n<p class=\"one-p\">疫情影响下，从中央到地方出台了一系列稳经济的好政策，各方在抓好疫情防控的同时，尽力提供更加精准式、“滴灌”式保障，化更大力度优化营商环境，下更大功夫抓政策落实，今年全年约1.5万亿元留抵退税就是最有说服力的例证之一。</p> \n<p class=\"one-p\">同时，近期一系列加快构建新发展格局、有助于建设强大而有韧性的国民经济循环体系的政策措施接连出台。《中共中央 国务院关于加快建设全国统一大市场的意见》发布，释放全面推动我国市场由大到强转变的鲜明改革信号；中央财经委员会第十一次会议研究全面加强基础设施建设、构建现代化基础设施体系；《关于进一步释放消费潜力促进消费持续恢复的意见》着力破除消费领域体制机制障碍……这些政策立足当前、着眼长远，彰显中国经济锚定高质量发展的定力。</p> \n<p class=\"one-p\">正如国家统计局新闻发言人付凌晖所说，疫情扰动不会改变我国经济平稳运行趋势，在各项政策支持下，国民经济将会加快恢复，实现企稳回升。</p> \n<p class=\"one-p\">最近，“中国这十年”系列主题新闻发布会陆续召开，回顾这十年，中国GDP占全球经济比重由11.4%升至18%以上，经济实力又迈上一个大台阶，其中面对新冠肺炎疫情冲击，2020年中国成为全球唯一正增长的主要经济体，2021年取得经济总量突破110万亿元人民币的超预期成绩。而如果把时间拉长为二十年，无论是面对2003年的非典疫情，还是2008年的国际金融危机，中国经济总能化险为夷、转危为机，彰显出应对风险、战胜挑战的强大能力。</p> \n<p class=\"one-p\">放眼看，中国经济巨轮航向明确、动力充沛，必将劈波斩浪，坚定地驶向高质量发展的彼岸。</p> \n<p class=\"one-p\">正所谓，长风破浪会有时，直挂云帆济沧海！</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471219291662', '1', null, null, '为全面推进乡村振兴夯实筑牢战斗堡垒', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A01UPE00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:12', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">民族要复兴，乡村必振兴；乡村要振兴，“领头雁”得配强。</p> \n<p class=\"one-p\">2020年10月，村“两委”任期由3年改为5年、31个省区市和新疆生产建设兵团集中进行、与县乡同步开展的首次换届开始。目前，全国49.1万个村班子顺利完成换届。</p> \n<p class=\"one-p\">干部群众普遍反映，这次换届要求严、力度大、风气正、效果好，选出了担当作为好班子，换出了安定团结好局面，形成了干事创业好氛围，为全面推进乡村振兴夯实筑牢了战斗堡垒。</p> \n<p class=\"one-p\">加强组织领导，层层压实换届工作责任</p> \n<p class=\"one-p\">抓好抓实换届工作，党的领导是关键。</p> \n<p class=\"one-p\">党中央、国务院高度重视此次村“两委”换届，习近平总书记多次作出重要指示批示，在赴有关地方考察时深入村里调研，对村干部提出殷切希望，关心关怀党员、群众，为各级党委认真履行管党治党责任、精心组织换届工作指明了方向，树立了榜样。</p> \n<p class=\"one-p\">中央组织部、民政部认真贯彻落实习近平总书记重要指示批示精神，指导推动各地抓好村“两委”换届工作。各级党委和政府扛起责任、抓紧抓实，保证了本次换届圆满完成、达到了预期目的。</p> \n<p class=\"one-p\">“县里镇里的干部常来村里，换届政策和纪律家喻户晓。”广东省佛冈县石角镇科旺村村民组长刘榕文说，换届工作开展后，干部白天没空就晚上来，工作日没空就周末来，耐心宣讲政策、指导工作。</p> \n<p class=\"one-p\">为压实责任，佛冈县成立了由县委书记担任组长的换届工作领导小组及办公室；全县推行领导干部联村包干机制，29名县领导“一竿子插到底”，每周分别带队下沉村(组)一线分析研判实情、排查化解矛盾、宣传换届纪律。</p> \n<p class=\"one-p\">各地强化党的领导，发挥组织优势，做细做实各项工作，确保换届平稳顺利推进。31个省区市和新疆生产建设兵团均成立由党委书记、副书记或组织部长任组长的换届领导小组，建立组织部门牵头协调、民政部门密切配合、有关部门共同参与的工作机制。北京、天津、上海、广东、重庆、新疆等省区市党委书记均深入村里调研，定期听取换届情况、推动工作扎实开展。</p> \n<p class=\"one-p\">同时，各地普遍实行省级领导联系市县、市级领导包县联乡、县级领导包乡联村、乡镇(街道)干部和驻村干部全员压上的指导推进机制，特别是推动县乡党委书记把换届工作紧紧抓在手上。</p> \n<p class=\"one-p\">“我们全面实行挂点包联制度，市领导全覆盖挂联90个县(市、区)，3000多名县级领导班子成员全面包乡走村，从准备工作开始到整个换届完成一包到底。”浙江省委组织部相关负责同志介绍，包联县领导、镇街联系领导和驻村干部“三人组团”，全程包干指导。</p> \n<p class=\"one-p\">在云南省巍山彝族回族自治县，乡镇党委逐村摸准摸实班子运转、党员现状、人选储备、群众诉求、风险隐患等情况，做到底数清楚、选情明晰；同时，县、乡(镇)、村党员干部到全县1341个村民小组召开群众会议，确保工作覆盖每个支部、每名党员和群众。</p> \n<p class=\"one-p\">组织领导坚强有力，换届工作有序开展。各地坚持党的领导、发扬民主、依法办事相统一，坚持目标导向、问题导向、结果导向相结合，高标准、高质量组织实施，把一系列有效举措落实到换届全过程。</p> \n<p class=\"one-p\">“下深水”选贤任能，让“好苗子”脱颖而出</p> \n<p class=\"one-p\">山东省临沂市罗庄区褚墩一村，一筐筐蔬菜正在流水线上密封装箱。1991年出生的退役军人张钰回村参选成为一名“兵支书”，敢闯敢干的他发挥特长，带领村党支部创办了电商合作社，依托“互联网+”、电子商务、直播等新兴业态，助推农产品销售驶上“电商经济”快车道，带动了村集体增收。</p> \n<p class=\"one-p\">人选的质量，决定着整个换届工作质量。为选出“好苗子”，山东省坚持把政治标准放在首位，让愿干事、真干事、干成事的优秀人才有机会有舞台。省里先后组织开展两轮分析研判，各地“下深水”选贤任能，实施农村在外优秀人才“回引计划”、后备人才“递进培养计划”，先后回引在外人才3.6万名。</p> \n<p class=\"one-p\">千秋基业，人才为本。选优配强干部是此次换届的重要工作。各地拓宽渠道，通过在现任村干部中“留”，在重大工作表现优秀的农村青年中“挑”，在致富带头人和优秀退伍军人中“选”，在外出务工能人和大学毕业生中“请”等途径，不拘一格选贤任能。</p> \n<p class=\"one-p\">“换届前，村干部普遍年龄偏大、学历偏低、能力偏弱。”广西梧州市长洲区委组织部相关负责人坦陈，区里在换届之初就注重从源头上严把选人关。长洲区抽调31名处级干部带队，对全区45个村逐个排查分析，开展谈心谈话，拿出“体检报告”，对835名初定人选的政治表现、履职尽责和廉洁自律等情况层层把关。同时，通过调研、党员群众举荐、机关单位选派、对外联络回引等举措，全面拓宽人才选拔渠道。</p> \n<p class=\"one-p\">“村里张贴的人选公示看到没？对哪些干部有印象？”“您认为该干部好在哪里？”换届工作组带着问题，挨家挨户收集群众意见建议，剔除“病秧子”，挑出“好苗子”，一批有能力、有担当的人脱颖而出。“由群众参加筛选，让我们真正体会到，金杯银杯不如老百姓的口碑。”新当选的倒水镇路垌村党支部书记、村委会主任黎柳松说。</p> \n<p class=\"one-p\">选优配强是目标，严格把关是保障。河北省唐山市细化负面清单，成立资格联审工作专班，出台资格联审办法，对所有意向人选层层把关，县乡资格预查预审30130人，清理不合格人选1389人。青海省祁连县通过村级把好“入口关”，乡镇把好“监督关”，县级严把“审查关”，对拟推荐的150名村党组织成员从任职资格、群众基础、道德品行等5个维度进行全面画像，防止“带病”推荐，“带病”当选。</p> \n<p class=\"one-p\">各地着眼于乡村振兴，好中选优、优中选强“领头雁”，一大批有志于乡村振兴的优秀人才进入村“两委”班子，为实现农村高质量发展注入了澎湃动力。</p> \n<p class=\"one-p\">数据显示，换届后村班子结构特别是带头人队伍实现整体优化：学历明显提升，村“两委”成员高中(中专)以上学历的占74%、提高16.7个百分点，村党组织书记大专以上学历的占46.4%、提高19.9个百分点。能人比例上升，村党组织书记中本村致富能手、外出务工经商返乡人员、大学毕业生、退役军人等致富带富能力较强的占73.6%，提高23.6个百分点。妇女成员上升，在村班子中占28.1%，提高7.1个百分点，每个村班子至少有1名妇女成员。年龄普遍下降，村“两委”成员平均年龄为42.5岁，下降5.9岁；村党组织书记平均年龄为45.4岁，下降3.9岁，有35岁以下班子成员的村占96.7%。</p> \n<p class=\"one-p\">人才聚起来，事业旺起来。学历高、干劲足、有活力的新班子，正以实干展现出新的气象。</p> \n<p class=\"one-p\">严纪律、明规矩，保证换届选举风清气正</p> \n<p class=\"one-p\">浙江省宁波市鄞州区东吴镇党委委员吴明霞对小白村村委会换届选举日的第一位投票者印象深刻：“天还没亮，80岁的林如宝老人就来到投票箱前，投出自己神圣的一票。”村里人习惯早起，投票前一天，工作人员就做好了充分准备，早早迎接选民的到来。</p> \n<p class=\"one-p\">为保证换届程序流畅，东吴镇推出了“两日历五流程”。“有了日历和流程，每天做什么、执行到哪个步骤一清二楚，操作起来很方便。”东吴镇党政办干部董莹莹说。</p> \n<p class=\"one-p\">严格依法依规推进，营造风清气正换届环境，直接关系换届工作之成败。各地严格遵循有关法律法规，紧盯选民登记、候选人提名、投票选举等关键节点，确保换届在政策框架和法律法规范围内健康有序进行。</p> \n<p class=\"one-p\">天津市指导各区分别制定换届工作流程图和民主推荐、选民登记等20余种规范性配套指南，提供行为规范和基本遵循。上海市下发指导手册，修订换届选举须知和工作规程，分层分类组织业务培训，累计组织培训5877次，覆盖28万人次。重庆市利用智慧治理平台，自动发布公告、计票、统计和生成电子档案，各区县、镇街动态跟踪掌握辖区选举进度……通过制作工作手册、选举模板、开展模拟演练、举办培训班等方式，各地有效提升了一线人员工作水平。</p> \n<p class=\"one-p\">各地还坚持教育在先、警示在先、预防在先，普遍开展换届纪律宣传和警示教育，签订纪律承诺书，确保换届选举风清气正。山西、内蒙古、黑龙江、江西、西藏等地通过广泛发放、张贴换届纪律宣传材料等途径，让党员群众明底线、知红线。吉林、安徽、四川、陕西和新疆生产建设兵团等地采取观看警示教育片、开展谈心谈话、签订纪律承诺书等多种方式开展换届纪律教育，打好“预防针”。</p> \n<p class=\"one-p\">本轮换届中，各地对村“两委”换届风气督导全覆盖，特别是加强对选举委员会推选、候选人提名、投票选举全程监督，架起“高压线”，念好“紧箍咒”。同时，各地建立换届信访举报快查快处机制，“12380”综合举报受理平台24小时畅通，对举报线索逐一核查，发现一起查处一起。</p> \n<p class=\"one-p\">“这次换届，每一步我们都看在眼里，规规矩矩，公开透明，选出来的书记脑子活、本事大、办事公，是群众‘心头上’的人，我们一百个支持，一百个放心。”青海省河南蒙古族自治县兰龙村老党员公保才让说。</p> \n<p class=\"one-p\">依法依规、正风肃纪，风清气正的换届环境为新老班子平稳交接打下了基础，也为新班子轻装上阵卸下了包袱。据统计，各地共查处涉及换届的违纪违法案件较上轮换届下降65.4%，收到信访件次下降62.1%。</p> \n<p class=\"one-p\">做好后半篇文章，推动新班子干事创业</p> \n<p class=\"one-p\">新班子磨合得怎么样？选举时的承诺落实得怎么样？换届结束3个月后，河南省郑州市全面启动初任评估，开展村党组织书记“亮承诺赛实绩比干劲”活动。</p> \n<p class=\"one-p\">2021年6月9日，郑州市举办“十佳”村党组织书记擂台赛，经层层筛选共有16名村党组织书记参赛。结合前期实地考察情况以及现场表现，评出“十佳”村党组织书记，市财政给予其所在村20万元奖励。对“百优”村党组织书记，由县级财政给予每个村10万元奖励，增加村级组织运转经费。</p> \n<p class=\"one-p\">观看了擂台赛，郑州市中原区后河卢村党委书记卢亚飞激动地说道，“这个舞台是为我们基层干部量体裁衣打造的，选出来的‘十佳’书记是我们学习的榜样，我知道该怎么干了!”</p> \n<p class=\"one-p\">换好是基础，干好是关键。各地抓配套、紧衔接、搞培训、强监督，做好换届后半篇文章，推动新班子尽快理清思路、进入角色。</p> \n<p class=\"one-p\">“咱们村有23个方塘，却一直没有利用。培训参观让我打开了思路。”辽宁省朝阳市建平县喀喇沁镇西村党支部书记王星，结束任职培训后立即组织村干部和党员群众代表座谈，大家为调整产业结构集思广益，谋划出一条涵盖水稻种植、方塘垂钓、民俗体验的休闲旅游发展思路。目前，西村依托稻田和方塘铺设甬道，建起了具有民俗特色的蒙古包，努力打造游客休闲度假的好去处。</p> \n<p class=\"one-p\">一些省区市通过开展履职培训、督促指导、健全机制等多种方式，推动新班子在全面推进乡村振兴中撸起袖子加油干。截至2022年2月，村“两委”成员已培训87%、86.2%，村党组织书记由省市两级培训的占60%，“两委”其他成员普遍由县一级轮训。湖南、湖北、海南、贵州、甘肃等地由省级开展村党组织书记大培训；福建、四川、宁夏等地结合“导师帮带制”试点，组织对新任村干部传帮带。</p> \n<p class=\"one-p\">江苏省南京市高淳区东坝街道和睦涧村，新任村干部全员包组包网入户，通过广泛征求群众建议，确定了环村道路提升、269亩抛荒地整治、7个公厕维修等8个项目作为第一批要办的事，紧锣密鼓实施……在全国各地，刚上任的新班子结合党史学习教育，办好“开门一件事”，以实干争取支持，以实绩赢得口碑。他们走遍家家户户听意见、问需求，确定攻坚事项、逐一抓好落实，在改善群众生产生活条件、提升村容村貌、发展特色产业等方面做实功、求实效。</p> \n<p class=\"one-p\">基层强则国家强，基层安则天下安。当前，新一届村“两委”班子接过了接力棒，广大村干部将在全面推进乡村振兴的新征程中踔厉奋发、笃行不怠，以新气象新担当新作为走好新的赶考之路，谱写无愧于历史、无愧于时代、无愧于人民的崭新篇章。</p> \n<p class=\"one-p\">《 人民日报 》( 2022年05月23日 04 版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471241518065', '1', null, null, '全国村“两委”集中换届完成', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A00LQL00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:12', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">本报北京5月22日电 (记者刘维涛、张洋、赵兵)在山东省成武县汶上集镇陈朱楼村村民们心里，时间仿佛加快了：村新班子上任两个月，排水沟疏通了，生产路加宽了，新装了净水机，新建了文化广场……“十件民生实事”件件做在心坎上。村党支部趁势领办合作社种植有机蔬菜，寂静的小村动了起来，一幅生动的乡村治理画卷铺展开来。</p> \n<p class=\"one-p\">记者从中央组织部获悉：自2020年10月开始的全国村“两委”换届工作目前已全部完成，49.1万个村班子完成新老更替。这是村“两委”任期由3年改为5年、31个省区市和新疆生产建设兵团集中进行、与县乡换届同步开展的首次换届。</p> \n<p class=\"one-p\">在如期打赢脱贫攻坚战、接续推进乡村振兴的关键节点，此次换届换出了新气象。各地村班子办好“开门一件事”、打好履职第一仗，发力全面推进乡村振兴。他们走遍家家户户，问准需求找对办法，修一段路，架一座桥，解一段陈年纠纷，引一个富民项目……乡村掀起干事热潮，到处是火热的发展景象。</p> \n<p class=\"one-p\">干得好，首先是选得好。此次换届，一大批懂发展善治理、有干劲会干事、群众认可的优秀人员成为村“两委”成员，村班子结构特别是带头人队伍实现整体优化。村“两委”成员高中(中专)以上学历的占74%、提高16.7个百分点，村党组织书记大专以上学历的占46.4%、提高19.9个百分点；村党组织书记中致富带富能力较强的占73.6%，提高23.6个百分点；村“两委”成员平均年龄为42.5岁、下降5.9岁，村党组织书记平均年龄45.4岁、下降3.9岁。</p> \n<p class=\"one-p\">各地严密组织，让换届成为党的领导在农村基层全面加强和为群众办实事解难题的过程，带来了安定团结好局面。换届前，全国普遍进行摸排、整顿、发动“三在先”，集中整顿3.6万个软弱涣散村党组织，解决了一大批群众急难愁盼问题。选举中，党员、群众参选率分别达92.7%、90.2%，村党组织、村委会一次性选举成功率分别达99.7%、98.3%；村党组织书记、村民委员会主任“一肩挑”比例达95.6%，比换届前提高29.5个百分点。同时，各地查处涉及换届的违纪违法案件数量较上轮换届下降65.4%，收到信访件次下降62.1%。</p> \n<p class=\"one-p\">干部群众普遍反映，这次换届要求严、力度大、风气正、效果好，充分体现了全面从严治党的历史性、开创性成就，为全面推进乡村振兴提供了坚强组织保证。</p> \n<p class=\"one-p\">当前，在广大农村地区，新班子干劲足，党员群众心气高，干群想在一起、干在一处，奋战在保障粮食安全、全面推进乡村振兴等重大任务一线，满怀豪情奔向现代化，越走越有奔头。</p> \n<p class=\"one-p\">《 人民日报 》( 2022年05月23日 01 版)</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471260580533', '1', null, null, '党旗在基层一线高高飘扬丨为全面推进乡村振兴夯实筑牢战斗堡垒——全国村“两委”换届工作顺利完成', null, null, null, '0', '2', 'https://new.qq.com/omn/20220522/20220522A06WEF00.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:13', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">新华社北京5月22日电<strong>题：为全面推进乡村振兴夯实筑牢战斗堡垒——全国村“两委”换届工作顺利完成</strong></p> \n<p class=\"one-p\">新华社记者</p> \n<p class=\"one-p\">民族要复兴，乡村必振兴；乡村要振兴，“领头雁”得配强。</p> \n<p class=\"one-p\">2020年10月，村“两委”任期由3年改为5年、31个省区市和新疆生产建设兵团集中进行、与县乡同步开展的首次换届开始。目前，全国49.1万个村班子顺利完成换届。</p> \n<p class=\"one-p\">干部群众普遍反映，这次换届要求严、力度大、风气正、效果好，选出了担当作为好班子，换出了安定团结好局面，形成了干事创业好氛围，为全面推进乡村振兴夯实筑牢了战斗堡垒。</p> \n<p class=\"one-p\"><strong>加强组织领导，层层压实换届工作责任</strong></p> \n<p class=\"one-p\">抓好抓实换届工作，党的领导是关键。</p> \n<p class=\"one-p\">党中央、国务院高度重视此次村“两委”换届，习近平总书记多次作出重要指示批示，在赴有关地方考察时深入村里调研，对村干部提出殷切希望，关心关怀党员、群众，为各级党委认真履行管党治党责任、精心组织换届工作指明了方向，树立了榜样。</p> \n<p class=\"one-p\">中央组织部、民政部认真贯彻落实习近平总书记重要指示批示精神，指导推动各地抓好村“两委”换届工作。各级党委和政府扛起责任、抓紧抓实，保证了本次换届圆满完成、达到了预期目的。</p> \n<p class=\"one-p\">“县里镇里的干部常来村里，换届政策和纪律家喻户晓。”广东省佛冈县石角镇科旺村村民组长刘榕文说，换届工作开展后，干部白天没空就晚上来，工作日没空就周末来，耐心宣讲政策、指导工作。</p> \n<p class=\"one-p\">为压实责任，佛冈县成立了由县委书记担任组长的换届工作领导小组及办公室；全县推行领导干部联村包干机制，29名县领导“一竿子插到底”，每周分别带队下沉村（组）一线分析研判实情、排查化解矛盾、宣传换届纪律。</p> \n<p class=\"one-p\">各地强化党的领导，发挥组织优势，做细做实各项工作，确保换届平稳顺利推进。31个省区市和新疆生产建设兵团均成立由党委书记、副书记或组织部长任组长的换届领导小组，建立组织部门牵头协调、民政部门密切配合、有关部门共同参与的工作机制。北京、天津、上海、广东、重庆、新疆等省区市党委书记均深入村里调研，定期听取换届情况、推动工作扎实开展。</p> \n<p class=\"one-p\">同时，各地普遍实行省级领导联系市县、市级领导包县联乡、县级领导包乡联村、乡镇（街道）干部和驻村干部全员压上的指导推进机制，特别是推动县乡党委书记把换届工作紧紧抓在手上。</p> \n<p class=\"one-p\">“我们全面实行挂点包联制度，市领导全覆盖挂联90个县（市、区），3000多名县级领导班子成员全面包乡走村，从准备工作开始到整个换届完成一包到底。”浙江省委组织部相关负责同志介绍，包联县领导、镇街联系领导和驻村干部“三人组团”，全程包干指导。</p> \n<p class=\"one-p\">在云南省巍山彝族回族自治县，乡镇党委逐村摸准摸实班子运转、党员现状、人选储备、群众诉求、风险隐患等情况，做到底数清楚、选情明晰；同时，县、乡（镇）、村党员干部到全县1341个村民小组召开群众会议，确保工作覆盖每个支部、每名党员和群众。</p> \n<p class=\"one-p\">组织领导坚强有力，换届工作有序开展。各地坚持党的领导、发扬民主、依法办事相统一，坚持目标导向、问题导向、结果导向相结合，高标准、高质量组织实施，把一系列有效举措落实到换届全过程。</p> \n<p class=\"one-p\"><strong>“下深水”选贤任能，让“好苗子”脱颖而出</strong></p> \n<p class=\"one-p\">山东省临沂市罗庄区褚墩一村，一筐筐蔬菜正在流水线上密封装箱。1991年出生的退役军人张钰回村参选成为一名“兵支书”，敢闯敢干的他发挥特长，带领村党支部创办了电商合作社，依托“互联网+”、电子商务、直播等新兴业态，助推农产品销售驶上“电商经济”快车道，带动了村集体增收。</p> \n<p class=\"one-p\">人选的质量，决定着整个换届工作质量。为选出“好苗子”，山东省坚持把政治标准放在首位，让愿干事、真干事、干成事的优秀人才有机会有舞台。省里先后组织开展两轮分析研判，各地“下深水”选贤任能，实施农村在外优秀人才“回引计划”、后备人才“递进培养计划”，先后回引在外人才3.6万名。</p> \n<p class=\"one-p\">千秋基业，人才为本。选优配强干部是此次换届的重要工作。各地拓宽渠道，通过在现任村干部中“留”，在重大工作表现优秀的农村青年中“挑”，在致富带头人和优秀退伍军人中“选”，在外出务工能人和大学毕业生中“请”等途径，不拘一格选贤任能。</p> \n<p class=\"one-p\">“换届前，村干部普遍年龄偏大、学历偏低、能力偏弱。”广西梧州市长洲区委组织部相关负责人坦陈，区里在换届之初就注重从源头上严把选人关。长洲区抽调31名处级干部带队，对全区45个村逐个排查分析，开展谈心谈话，拿出“体检报告”，对835名初定人选的政治表现、履职尽责和廉洁自律等情况层层把关。同时，通过调研、党员群众举荐、机关单位选派、对外联络回引等举措，全面拓宽人才选拔渠道。</p> \n<p class=\"one-p\">“村里张贴的人选公示看到没？对哪些干部有印象？”“您认为该干部好在哪里？”换届工作组带着问题，挨家挨户收集群众意见建议，剔除“病秧子”，挑出“好苗子”，一批有能力、有担当的人脱颖而出。“由群众参加筛选，让我们真正体会到，金杯银杯不如老百姓的口碑。”新当选的倒水镇路垌村党支部书记、村委会主任黎柳松说。</p> \n<p class=\"one-p\">选优配强是目标，严格把关是保障。河北省唐山市细化负面清单，成立资格联审工作专班，出台资格联审办法，对所有意向人选层层把关，县乡资格预查预审30130人，清理不合格人选1389人。青海省祁连县通过村级把好“入口关”，乡镇把好“监督关”，县级严把“审查关”，对拟推荐的150名村党组织成员从任职资格、群众基础、道德品行等5个维度进行全面画像，防止“带病”推荐，“带病”当选。</p> \n<p class=\"one-p\">各地着眼于乡村振兴，好中选优、优中选强“领头雁”，一大批有志于乡村振兴的优秀人才进入村“两委”班子，为实现农村高质量发展注入了澎湃动力。</p> \n<p class=\"one-p\">数据显示，换届后村班子结构特别是带头人队伍实现整体优化：学历明显提升，村“两委”成员高中（中专）以上学历的占74%、提高16.7个百分点，村党组织书记大专以上学历的占46.4%、提高19.9个百分点。能人比例上升，村党组织书记中本村致富能手、外出务工经商返乡人员、大学毕业生、退役军人等致富带富能力较强的占73.6%，提高23.6个百分点。妇女成员上升，在村班子中占28.1%，提高7.1个百分点，每个村班子至少有1名妇女成员。年龄普遍下降，村“两委”成员平均年龄为42.5岁，下降5.9岁；村党组织书记平均年龄为45.4岁，下降3.9岁，有35岁以下班子成员的村占96.7%。</p> \n<p class=\"one-p\">人才聚起来，事业旺起来。学历高、干劲足、有活力的新班子，正以实干展现出新的气象。</p> \n<p class=\"one-p\"><strong>严纪律、明规矩，保证换届选举风清气正</strong></p> \n<p class=\"one-p\">浙江省宁波市鄞州区东吴镇党委委员吴明霞对小白村村委会换届选举日的第一位投票者印象深刻：“天还没亮，80岁的林如宝老人就来到投票箱前，投出自己神圣的一票。”村里人习惯早起，投票前一天，工作人员就做好了充分准备，早早迎接选民的到来。</p> \n<p class=\"one-p\">为保证换届程序流畅，东吴镇推出了“两日历五流程”。“有了日历和流程，每天做什么、执行到哪个步骤一清二楚，操作起来很方便。”东吴镇党政办干部董莹莹说。</p> \n<p class=\"one-p\">严格依法依规推进，营造风清气正换届环境，直接关系换届工作之成败。各地严格遵循有关法律法规，紧盯选民登记、候选人提名、投票选举等关键节点，确保换届在政策框架和法律法规范围内健康有序进行。</p> \n<p class=\"one-p\">天津市指导各区分别制定换届工作流程图和民主推荐、选民登记等20余种规范性配套指南，提供行为规范和基本遵循。上海市下发指导手册，修订换届选举须知和工作规程，分层分类组织业务培训，累计组织培训5877次，覆盖28万人次。重庆市利用智慧治理平台，自动发布公告、计票、统计和生成电子档案，各区县、镇街动态跟踪掌握辖区选举进度……通过制作工作手册、选举模板、开展模拟演练、举办培训班等方式，各地有效提升了一线人员工作水平。</p> \n<p class=\"one-p\">各地还坚持教育在先、警示在先、预防在先，普遍开展换届纪律宣传和警示教育，签订纪律承诺书，确保换届选举风清气正。山西、内蒙古、黑龙江、江西、西藏等地通过广泛发放、张贴换届纪律宣传材料等途径，让党员群众明底线、知红线。吉林、安徽、四川、陕西和新疆生产建设兵团等地采取观看警示教育片、开展谈心谈话、签订纪律承诺书等多种方式开展换届纪律教育，打好“预防针”。</p> \n<p class=\"one-p\">本轮换届中，各地对村“两委”换届风气督导全覆盖，特别是加强对选举委员会推选、候选人提名、投票选举全程监督，架起“高压线”，念好“紧箍咒”。同时，各地建立换届信访举报快查快处机制，“12380”综合举报受理平台24小时畅通，对举报线索逐一核查，发现一起查处一起。</p> \n<p class=\"one-p\">“这次换届，每一步我们都看在眼里，规规矩矩，公开透明，选出来的书记脑子活、本事大、办事公，是群众‘心头上’的人，我们一百个支持，一百个放心。”青海省河南蒙古族自治县兰龙村老党员公保才让说。</p> \n<p class=\"one-p\">依法依规、正风肃纪，风清气正的换届环境为新老班子平稳交接打下了基础，也为新班子轻装上阵卸下了包袱。据统计，各地共查处涉及换届的违纪违法案件较上轮换届下降65.4%，收到信访件次下降62.1%。</p> \n<p class=\"one-p\"><strong>做好后半篇文章，推动新班子干事创业</strong></p> \n<p class=\"one-p\">新班子磨合得怎么样？选举时的承诺落实得怎么样？换届结束3个月后，河南省郑州市全面启动初任评估，开展村党组织书记“亮承诺赛实绩比干劲”活动。</p> \n<p class=\"one-p\">2021年6月9日，郑州市举办“十佳”村党组织书记擂台赛，经层层筛选共有16名村党组织书记参赛。结合前期实地考察情况以及现场表现，评出“十佳”村党组织书记，市财政给予其所在村20万元奖励。对“百优”村党组织书记，由县级财政给予每个村10万元奖励，增加村级组织运转经费。</p> \n<p class=\"one-p\">观看了擂台赛，郑州市中原区后河卢村党委书记卢亚飞激动地说道，“这个舞台是为我们基层干部量体裁衣打造的，选出来的‘十佳’书记是我们学习的榜样，我知道该怎么干了！”</p> \n<p class=\"one-p\">换好是基础，干好是关键。各地抓配套、紧衔接、搞培训、强监督，做好换届后半篇文章，推动新班子尽快理清思路、进入角色。</p> \n<p class=\"one-p\">“咱们村有23个方塘，却一直没有利用。培训参观让我打开了思路。”辽宁省朝阳市建平县喀喇沁镇西村党支部书记王星，结束任职培训后立即组织村干部和党员群众代表座谈，大家为调整产业结构集思广益，谋划出一条涵盖水稻种植、方塘垂钓、民俗体验的休闲旅游发展思路。目前，西村依托稻田和方塘铺设甬道，建起了具有民俗特色的蒙古包，努力打造游客休闲度假的好去处。</p> \n<p class=\"one-p\">一些省区市通过开展履职培训、督促指导、健全机制等多种方式，推动新班子在全面推进乡村振兴中撸起袖子加油干。截至2022年2月，村“两委”成员已培训87%、86.2%，村党组织书记由省市两级培训的占60%，“两委”其他成员普遍由县一级轮训。湖南、湖北、海南、贵州、甘肃等地由省级开展村党组织书记大培训；福建、四川、宁夏等地结合“导师帮带制”试点，组织对新任村干部传帮带。</p> \n<p class=\"one-p\">江苏省南京市高淳区东坝街道和睦涧村，新任村干部全员包组包网入户，通过广泛征求群众建议，确定了环村道路提升、269亩抛荒地整治、7个公厕维修等8个项目作为第一批要办的事，紧锣密鼓实施……在全国各地，刚上任的新班子结合党史学习教育，办好“开门一件事”，以实干争取支持，以实绩赢得口碑。他们走遍家家户户听意见、问需求，确定攻坚事项、逐一抓好落实，在改善群众生产生活条件、提升村容村貌、发展特色产业等方面做实功、求实效。</p> \n<p class=\"one-p\">基层强则国家强，基层安则天下安。当前，新一届村“两委”班子接过了接力棒，广大村干部将在全面推进乡村振兴的新征程中踔厉奋发、笃行不怠，以新气象新担当新作为走好新的赶考之路，谱写无愧于历史、无愧于时代、无愧于人民的崭新篇章。</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471280891176', '1', null, null, '为何强调老年人加强新冠疫苗接种？', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A05VC000.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:13', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">新冠病毒的危害有没有减轻？疫苗接种有没有作用？为何强调老年人加强疫苗接种？近日，《面对面》专访了中国疾控中心免疫规划首席专家王华庆。王华庆有着30多年免疫工作经历，自开展新冠疫苗接种以来，一直跟踪国内外新冠病毒疫苗研发和应用的进展，他主要参与新冠疫苗接种科普信息传播、不良反应监测、效果评估研究等多项研究工作。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612530/1000\"> </p> \n<p class=\"one-p\">根据国家卫生健康委公布的消息，截至2022年5月21日，全国31个省（自治区、直辖市）和新疆生产建设兵团累计报告接种新冠病毒疫苗337016.2万剂次。从2020年12月首批新冠疫苗开始对重点人群进行接种，一年半时间内，中国完成了世界上人数规模最大的新冠病毒疫苗接种。</p> \n<p class=\"one-p\">王华庆告诉记者，针对两年前新冠肺炎原始毒株开发的疫苗，随着疫苗接种时间的推移，中和抗体呈下降趋势，德尔塔变异株和奥密克戎变异株也出现了免疫逃逸现象。</p> \n<p class=\"one-p\">记者：怎么面对这种意外？</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612531/1000\"> </p> \n<p class=\"one-p\">王华庆：目前这个情况下主要就是说怎么样减少重症的发生，减少死亡的发生。（接种疫苗）这是目前我们能够采取一个最好的方法，因为我们看到不管之前的德尔塔变异株也好，还是到目前的奥密克戎也好，重症死亡的发生情况在一些国家和一些地区也比较严重，在这种情况下，其实打疫苗的一个目的主要是减少重症、减少死亡。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612532/1000\"> </p> \n<p class=\"one-p\">记者：现在从医学研究数据来看的话，通过疫苗的接种降低死亡率或者减轻重症率，这个必然之间的联系是怎么样的？</p> \n<p class=\"one-p\">王华庆：现在关于疫苗，从全球来说达成一个共识还要继续接种，尤其是要给老年人接种，给慢性病人群接种，还要进行加强针，其实还是看到疫苗在预防重症和预防死亡的效果。效果其实有两个方面，第一个减少死亡和重症的风险能够达到多少，还有不接种疫苗和接种不同剂次的疫苗从人群的发病率趋势来做一个比较，其实这两个方面都看到了疫苗预防重症和死亡的效果。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612534/1000\"> </p> \n<p class=\"one-p\">根据香港卫生署5月12日公布的信息，从2021年12月23日至2022年5月11日，香港暴发新冠肺炎疫情期间，不接种疫苗的人群病死率是3.04%，接种两剂次疫苗的病死率为0.17%，接种三剂次的病死率是0.04%，后两者与不接种人群相比分别相差18倍和76倍。另外，疾控机构对今年春季吉林市暴发聚集性疫情的病例进行了专项研究，研究结果显示，重症病例中没有接种疫苗和没有全程接种疫苗的人数占到78%。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612535/1000\"> </p> \n<p class=\"one-p\">相关研究表明，近期发生在香港、上海等地的新冠肺炎疫情所导致的重症和死亡病例，除了未接种疫苗或未全程接种疫苗的占比较大之外，还有高龄者多、有慢性病者多的明显特征。</p> \n<p class=\"one-p\">王华庆：我们看到重症和死亡主要是在有慢性基础性疾病的和老年人当中出现的比例非常大，为什么在这些人群出现了重症和死亡的比较高。感染病毒是一个非常重要的诱因，一方面使患上新冠肺炎的症状更重一些，另外一个其实感染了新冠病毒，导致原有基础性疾病进一步加重，而且会使他严重的合并症进一步增加。因为新冠病毒从早期研究结果来看可以侵犯大多数组织和器官，这些组织和器官原先处在一个病理状况的时候，再有这个病毒攻击的时候就会更重一些。另外对于老年人来说还有一个风险，他感染之后病程比较隐匿，在其他人当中可能发烧会马上凸显出来，发烧之后知道可能有感染的风险，但是老年人发烧率和其他人相比比较低。</p> \n<p class=\"one-p\">记者：为什么会出现这种情况？</p> \n<p class=\"one-p\">王华庆：生理基础决定了老年人反应弱，疾病临床表现过程就和普通人不一样，所以在这种情况下有一些老年人失去生命，其实跟发现晚有关系。因为症状不典型，一旦出现症状的时候可能就比较严重了。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612537/1000\"> </p> \n<p class=\"one-p\">据香港卫生署统计，香港本轮新冠肺炎疫情中，80岁以上老年人的病死率为16.48%。现有的研究数据表明，新冠肺炎病毒对老年人的危害，并没有减弱的迹象。</p> \n<p class=\"one-p\">王华庆：现在病毒在进化，我们看到奥密克戎目前传染性特别强，传播速度又快，传播过程又隐匿。我们没打疫苗，感染之后，尤其老年人其实带来这种重症和死亡的风险在增加。在这种情况下，疫苗对于他们降低重症和死亡其实是最好的一个措施。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612539/1000\"> </p> \n<p class=\"one-p\">目前，在我国“坚持动态清零”全方位综合防控新冠肺炎疫情的阶段，如何进一步加强疫苗接种，减少老年人因感染导致重症和死亡的几率，成为防控工作的重点之一。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612540/1000\"> </p> \n<p class=\"one-p\">王华庆：因为我们已经进入老龄化社会了，老年人口在我们人群当中占的比例非常大。这种疾病广泛流行起来的时候，带来的冲击我想可能还是比较大的。从世界卫生组织也好，各个国家也好，制定了一个最优先接种的人群，这里面就包括60岁以上的老年人，有的国家可能要定在65岁或75岁以上的人群。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612541/1000\"> </p> \n<p class=\"one-p\">根据国家卫生健康委的通报，截至5月12日，全国60岁以上老年人新冠病毒疫苗接种约2.3亿人，接种覆盖率86%、全程接种率82%，与全国人口接种覆盖率91%、全程接种率89%相比，依然偏低。</p> \n<p class=\"one-p\">记者：其实现在已经达到80%多这个比例，还要继续加强对老年人的疫苗接种？</p> \n<p class=\"one-p\">王华庆：这是一个全国平均数，但是如果把这个数分散到各地，我们就看到地区间差异比较明显，这个病毒出现感染的时候，对于接种率比较低的地方，像80岁以上人群其实风险就比较大了。</p> \n<p class=\"one-p\">记者：从这个接种的角度来看的话是不是这个相当于木桶理论里面的短板？</p> \n<p class=\"one-p\">王华庆：对，它现在有两个短板，第一个就是我们看到有些地区老年人接种不高，第二个短板就是年龄。在60~69岁这个人群接种率可能接近90%或者90%左右，但是如果到了70~80岁，尤其是80岁以上的时候可能有的都达不到50%或60%，这个时候出现疾病流行，大家想一想带来重症和死亡的风险就会非常大。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612542/1000\"> </p> \n<p class=\"one-p\">除了疫苗接种能够有效减少重症和死亡病例之外，治疗新冠肺炎的特效药也被寄予厚望。截至5月8日，国内已批准6个新冠病毒治疗药物上市。</p> \n<p class=\"one-p\">记者：对很多人来讲的话会觉得可能病毒的危害性现在已经不像最初那么强烈了，在这种情况下还必须要接种这种疫苗吗？</p> \n<p class=\"one-p\">王华庆：我们说疾病严重不严重的时候一个看发病数，还有一个看发病的人群当中病死比例有多大，还包括重症比例有多大。这次奥密克戎其实发病数在新冠流行的两年多来，从全球的角度发病数是大大超过之前的高峰，每周高峰发病数其实和以前最高峰相比可能超过四五倍的程度。在这样一个庞大基数下，我们看到病死情况也出现一个高峰。像我们国家所在的西太平洋区，病死高峰和之前流行的德尔塔病死高峰其实是差不多少的。有人有很多顾虑不接种疫苗，但是真正流行起来的时候，我们看到不接种疫苗带来这个危害是比较严重的。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612543/1000\"> </p> \n<p class=\"one-p\">针对不断变异的新冠病毒，科研人员致力于推动疫苗的迭代升级，希望能够研制出更有效的疫苗。5月9日，中国首支奥密克戎株新冠病毒灭活疫苗序贯临床试验正式启动，这也是全球首支进入临床试验，针对奥密克戎变异株的新冠病毒灭活疫苗。</p> \n<p class=\"one-p\">记者：对很多需要接种的老年人来讲，他们会觉得更成熟的时候接种可能是最安全的，能这么理解吗？</p> \n<p class=\"one-p\">王华庆：我想是不能这么理解的，奥密克戎变异株的疫苗现在还在研发阶段，将来要做一系列评估还需要一定时间。在这种情况下未来是一个怎么样的情况，我们现在要做的是把眼前的事情做好，用现有疫苗可以起到预防重症、预防死亡比较好的效果，我们现在为什么不用呢？</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612544/1000\"> </p> \n<p class=\"one-p\">在新冠疫苗接种的过程中，也有一些老年人因为担心疫苗可能会发生的不良反应而迟迟没有接种。</p> \n<p class=\"one-p\">王华庆：严重的不良反应发生大概是在百万分之一这样一个水平，或者比这个更低的水平。现在根据我们分析，和既往使用的疫苗相比，不高于以前用过的疫苗严重不良反应的发生水平。</p> \n<p class=\"one-p\">记者：可能老年人的免疫系统还有各个方面基础比较薄弱，这种不良反应的存在是否会对他们也带来一定影响？</p> \n<p class=\"one-p\">王华庆：我们一直在倡导大家去了解认识风险和获益，假如说我们一直感受不到疾病威胁的话，大家可能考虑的主要是这个问题。但是如果说感染这个病毒带来的风险80岁以上病死率超过16%和我们不良反应百万分之一相比的话，其实可以看到非常巨大的一个差距。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612545/1000\"> </p> \n<p class=\"one-p\">王华庆介绍说，为了解决疫苗接种者的后顾之忧，对于新冠疫苗接种出现的不良反应，国家设有健全的异常反应补偿机制进行保障。</p> \n<p class=\"one-p\">王华庆：好比说我是受种者，我打完疫苗之后出现发烧，发烧时间比较长，而且发烧也比较高，在这种情况下我怀疑是疫苗，你就要向接种单位报告。接种单位接到这个报告之后如果你的症状没有缓解，发烧没有降下来，又出现了其他症状的时候要报告给疾控中心，疾控中心要组织相关的专家来去做调查和诊断，来看是偶合症还是不良反应。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612547/1000\"> </p> \n<p class=\"one-p\">记者：如果说注射疫苗产生了对老年人身体不利的影响甚至是损害，谁来承担责任？</p> \n<p class=\"one-p\">王华庆：对于这些情况，各地都按疫苗管理法的相关要求。好比说经费是从哪出，赔偿标准是哪些，我们有一套完整机制来去做这个事情。在我们国家疫苗接种这么多年的过程中，已经形成了一个比较完整的机制。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14931612549/1000\"> </p> \n<p class=\"one-p\">在党中央国务院的决策部署下，目前我国各地正在按照“应接尽接”的原则，有序推进新冠病毒疫苗同源和序贯加强免疫接种。全国人口，尤其是60岁以上老年人群的疫苗接种率将进一步得到提升。</p> \n<p class=\"one-p\">王华庆：根据我们从专业角度来说，接种率越高的话，风险就会进一步降低。老年人不打疫苗，接种率比较低的话，到时候流行起来的话重症可能就会出现很多，病死的情况也会进一步增加。</p> \n<p class=\"one-p\">记者：您现在的建议是什么？</p> \n<p class=\"one-p\">王华庆：我们建议符合条件的老年人尽快去接种疫苗，不要等着疫情来了，在我们周边发生感染了，我们再想接种疫苗，那么这个时候可能已经晚了。有些时候感染这个疾病，得了重症，或者家人出现失去生命的时候，我们想是不是早期时候有一种特效药会更好，现在来看不让重症发生、减少死亡的发生，这个特效药其实就是疫苗。</p> \n<p class=\"one-p\">编辑/倪家宁</p> \n<div id=\"Status\"></div>', null);
INSERT INTO `cms_article` VALUES ('2022052321471304642525', '1', null, null, '上海国际航运中心保稳保畅一线观察', null, null, null, '0', '2', 'https://new.qq.com/omn/20220523/20220523A07V4100.html', null, null, '0', '0', '0', '0', '0', '0', '0', '0', '0', null, null, '2022-05-23 21:47:13', null, '1', '0', null, null, null, '<!--导语--> \n<p class=\"one-p\">在洋山深水港,远洋巨轮停靠一字排开,装卸作业繁忙有序;在长兴岛造船基地,清洁能源运输船按期交付、整装待发;在浦东国际机场,C919大飞机试飞试验圆满成功……自此轮疫情发生以来,上海国际航运中心这一“远洋旗舰”,努力守住疫情防控的阵地,稳住保稳保畅的通道,擦亮先进制造的引擎,在疫情防控与生产运行“双线战役”中稳扎稳打,凭借十足韧性破浪前行。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14932264402/1000\"> </p> \n<p class=\"one-p\">中国商飞上海飞机制造有限公司总装车间内的国产ARJ21飞机。记者 丁汀 摄</p> \n<p class=\"one-p\">不停不乱保“生产”</p> \n<p class=\"one-p\">“2万余名员工在港区封控作业近2个月,不舍昼夜守好阵地,保证装卸生产不停不乱。”上港集团生产业务部总经理助理周勇说。</p> \n<p class=\"one-p\">疫情冲击突如其来,全球第一大集装箱码头上海港面临着前所未有的挑战。3月底,在上海港区实施封控作业管理措施的前一晚,2万余名员工接到紧急通知,从四面八方赶到港区集中办公,他们的坚守保障了上海港24小时有序作业,也让生产、贸易企业心中有底。</p> \n<p class=\"one-p\">疫情防控“战时”状态下,干扰物流保障的不少新问题出现:公路运输受阻、集装箱卡车通行困难、核酸检测跟不上通行需求、冷链箱堆存量骤增……一时间,有序运转的港区面临重重挑战。</p> \n<p class=\"one-p\"><img class=\"content-picture\" src=\"//inews.gtimg.com/newsapp_bt/0/14932264403/1000\"> </p> \n<p class=\"one-p\">上海洋山深水港四期自动化码头。记者 丁汀 摄</p> \n<p class=\"one-p\">办法总比困难多。疫情下,集卡司机“上路难”,上海港开发集纳核酸、行程码等信息的电子“防疫通行证”,集卡司机只要手机端查验通过即可通行,既保证了车辆通行效率,也确保了港区的防疫安全。“为帮助他们解决生活上的难题,从4月11日起,上海港8个集装箱码头每天为进出港的集卡司机提供免费餐食3000余份。”周勇说。</p> \n<p class=\"one-p\">为应对公路运输受阻的压力,中远海运、中国铁路上海局积极行动起来,增加水水中转、海铁联运比重,保证了物流链主动脉的畅通。</p> \n<p class=\"one-p\">集疏运综合优势彰显了上海国际航运中心应对疫情冲击的十足韧性,而这样的韧性来自市场中的千锤百炼。十多年来,上海国际航运中心建设始终在搏击风浪中成长。自2020年以来,疫情考验让上海国际航运中心增强了应对疫情风险的预判力和行动力,在“疫情大战”中沉着应对、牢牢坚守。</p> \n<p class=\"one-p\">未雨绸缪保“按期”</p> \n<p class=\"one-p\">近来,“上天”“入海”的好消息给上海国际航运中心建设提振了士气。</p> \n<p class=\"one-p\">5月14日,C919大飞机即将交付客户的首架机首次试飞成功,一时间成为公众关注的热点新闻。其实,不为人知的是,在疫情防控的严峻形势下,为试飞试验提供保障的物流运输、人员流动、试验安排等都受到不同程度的影响。为保证试验进度,早在2月底,该项目的核心人员已在园区集中办公,为试验成功提供了坚实保障。</p> \n<p class=\"one-p\">未雨绸缪的还有中国船舶集团旗下的江南造船。4月23日,江南造船进入复工复产压力测试阶段,万人大厂的疫情防控和安全生产管理难度不小,因此工作流程、就餐住宿等均进行了全面细致的安排。在疫情防控扎实有效的保障下,5月16日,江南造船建造的超大型乙烷运输船按期交付。江南造船生产运行二部副部长袁世振介绍,这个“按期”殊为不易,是在严格防疫和生产安全的双重保障下抢出的速度。</p> \n<p class=\"one-p\">这些彰显上海国际航运中心先进制造优势的装备制造“大块头”,在疫情之下,无论科研、生产还是试验、运营都底气十足,这样的底气正来自始终如一的攻坚克难硬核实力。</p> \n<p class=\"one-p\">平稳运行赢“信赖”</p> \n<p class=\"one-p\">不可否认的是,疫情已经对国际航运市场造成不小冲击,物流效率、新船建造、航线调整、运价攀升都让人们对航运市场的平稳运行产生担忧。而此时的上海国际航运中心以稳定运行赢得了国际市场的信赖。</p> \n<p class=\"one-p\">受疫情影响,上海港集装箱吞吐量一度出现下滑,但进入5月份以来,单日集装箱吞吐量稳步回升,已突破11万标准箱。</p> \n<p class=\"one-p\">上海机场集团数据显示,5月以来,上海浦东机场货邮吞吐量持续回升,日均保障货运航班118架次,日均货运量5300吨以上,比4月同期分别提高37%和64%。东航、吉祥航空、春秋航空等基地在上海的航空公司,已在陆续恢复航班。</p> \n<p class=\"one-p\">今年1月至5月15日,江南造船承接订单金额达236亿元,同比2021年增长76%,足见全球航运市场对中国造船业信心十足。</p> \n<p class=\"one-p\">随着企业复工复产、复商复市稳步推进,上海国际航运中心已为生产、贸易企业的正常运转做好了准备。(记者 贾远琨 上海报道)</p> \n<p class=\"one-p\">终审:张松</p> \n<p class=\"one-p\">责编:刘艳</p> \n<div id=\"Status\"></div>', null);

-- ----------------------------
-- Table structure for `spider_config`
-- ----------------------------
DROP TABLE IF EXISTS `spider_config`;
CREATE TABLE `spider_config` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '爬虫配置ID',
  `spider_code` varchar(50) DEFAULT NULL COMMENT '爬虫编码',
  `spider_name` varchar(50) DEFAULT NULL COMMENT '爬虫名称',
  `entry_urls` text COMMENT '入口地址',
  `target_regex` varchar(255) DEFAULT NULL COMMENT '目标URL正则',
  `cascade` smallint(6) DEFAULT '0' COMMENT '1:级联发现url  0:只从入口页面发现url',
  `table_name` varchar(50) DEFAULT NULL COMMENT '存储的表名',
  `domain` varchar(255) DEFAULT NULL COMMENT '网站根域名',
  `charset` varchar(50) DEFAULT 'utf8' COMMENT '字符集',
  `sleep_time` int(11) DEFAULT '1000' COMMENT '睡眠时间(ms)',
  `retry_times` smallint(6) DEFAULT '1' COMMENT '重试次数',
  `thread_count` smallint(255) DEFAULT '1' COMMENT '线程数量',
  `use_proxy` smallint(6) DEFAULT '0' COMMENT '使用代理',
  `show_log` smallint(6) DEFAULT '1' COMMENT '打印日志',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of spider_config
-- ----------------------------
