-- ----------------------------
-- Table structure for `spider_mission`
-- ----------------------------
DROP TABLE IF EXISTS `spider_mission`;
CREATE TABLE `spider_mission` (
  `mission_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '鐖櫕浠诲姟ID',
  `mission_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '浠诲姟鍚嶇О',
  `spider_config_id` int(11) DEFAULT NULL COMMENT '鐖櫕閰嶇疆ID',
  `spider_config_id_name` varchar(55) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `entry_urls` text CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '鍏ュ彛鍦板潃',
  `status` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '浠诲姟鐘舵€?',
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '结束时间',
  `time_cost` mediumtext COMMENT '爬取时间(单位秒)',
  `exit_way` varchar(50) DEFAULT NULL COMMENT '退出方式。DEFAULT，DURATION，URL_COUNT。',
  `exit_way_count` int(11) DEFAULT NULL COMMENT '退出方式值',
  `success_num` int(11) DEFAULT NULL COMMENT '爬取数量',
  `cookie_str` text,
  `header_str` text,
  `dept_id` varchar(255) DEFAULT NULL COMMENT '部门ID',
  `user_id` varchar(255) DEFAULT NULL COMMENT '用户ID',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT NULL,
  PRIMARY KEY (`mission_id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8;

